   var _loc6_ = new JSON();
   _root.json多语言数据 = _loc6_.parse(多语言json文档);
}
function 获得翻译(key)
{
   if(key == "")
   {
      return key;
   }
   var _loc3_ = _root.json多语言数据[key];
   if(_loc3_ == undefined)
   {
      return key;
   }
   return _loc3_;
}
function translateStrings(strings)
{
   var _loc2_ = 0;
   var _loc3_ = "";
   while(_loc2_ < strings.length)
   {
      _loc3_ += 获得翻译(String(strings[_loc2_]));
      _loc2_ += 1;
   }
   return _loc3_;
}
function 获得翻译或不显示(key)
{
   if(key == "")
   {
      return key;
   }
   var _loc3_ = _root.json多语言数据[key];
   if(_loc3_ == undefined)
   {
      return "";
   }
   return _loc3_;
}
function 全部翻译(mc)
{
   for(each in mc)
   {
      if(mc[each].text != undefined)
      {
         if(mc[each]._name.indexOf("段落_") > -1)
         {
            var _loc3_ = mc[each]._name;
            var _loc4_ = _root.获得翻译(_loc3_);
            if(_loc4_ != _loc3_)
            {
               mc[each].text = _loc4_;
            }
         }
         else
         {
            _loc4_ = _root.获得翻译(mc[each].text);
            if(_loc4_ != mc[each].text)
            {
               mc[each].text = _loc4_;
            }
         }
      }
   }
}
