function 获得翻译(key){
	return key;
}
function translateStrings(strings){
	return strings.join("");
}
function 获得翻译或不显示(key){
	return key;
}

// 新建 XML 对象并忽略空白节点
var cfgXML:XML = new XML();
cfgXML.ignoreWhite = true;

// 加载完成回调
cfgXML.onLoad = function(success:Boolean):Void {
    if (!success) {
        _root.欢迎语 = "配置加载失败，请检查 config.xml";
        return;
    }

    // 1. 收集所有字段到一个 key->value 的对象里
    var placeholders:Object = {};
    var nodes:Array = this.firstChild.childNodes;
    for (var i:Number = 0; i < nodes.length; i++) {
        var node = nodes[i];
        // node.firstChild.nodeValue 即内层文本
        placeholders[node.nodeName] = node.firstChild.nodeValue;
    }

    // 2. 取出模板
    var tpl:String = placeholders["template"];
    if (tpl == undefined) {
        _root.欢迎语 = "找不到模板(template)节点";
        return;
    }

    // 3. 遍历 placeholders，逐个替换模板中的 {key}
    for (var key:String in placeholders) {
        if (key == "template") continue;
        // 生成 "{key}" 形式
        var tag:String = "{" + key + "}";
        // 反复用 split/join 来全局替换
        tpl = tpl.split(tag).join(placeholders[key]);
    }

    // 4. 填入到舞台
    _root.欢迎语 = tpl;
};

// 开始加载（确保 config.xml 与 SWF 在同一路径）
cfgXML.load("config.xml");


