on (press) {
	this.gotoAndStop(3);

	trace("=== 开始导入操作 ===");
	trace("当前savePath: " + _root.savePath);
	trace("exportedSaveString长度: " + (_root.exportedSaveString ? _root.exportedSaveString.length : "null"));

	if (!_root.exportedSaveString)
	{
		trace("没有可导入的存档数据！请先选择一个有数据的存档位");
		return false;
	}

	var saveData = _root.工具用存档块.deserializeSaveData(_root.exportedSaveString);

	if (saveData)
	{
		var mysave = SharedObject.getLocal(_root.savePath);
		mysave.data["test"] = saveData;

		// 获取当前时间并格式化为字符串（与数据组包函数保持一致）
		var now:Date = new Date();
		var 年 = now.getFullYear();
		var 月 = now.getMonth() + 1;
		var 日 = now.getDate();
		var 时 = now.getHours();
		var 分 = now.getMinutes();
		var 秒 = now.getSeconds();

		//  补零函数
		this.pad = function(n)
		{
			trace(n)
			return (n < 10) ? "0" + n : n;
		}

		var lastSaved:String = 年 + "-" + pad(月) + "-" + pad(日) + " " + pad(时) + ":" + pad(分) + ":" + pad(秒);

		mysave.data["test"].lastSaved = lastSaved;
		mysave.flush();
		trace("存档导入成功");

		// 强制刷新显示
		for (var i = 0; i < 10; i++)
		{
			var slot = _root["存档位" + i];
			if (slot && slot.currentSavePath == _root.savePath)
			{
				slot.savePath = "";// 强制重新加载
				break;
			}
		}
		return true;
	}
	else
	{
		trace("存档导入失败：数据格式错误");
		return false;
	}
}

