on (release) {
	if (this.确认按钮是否失效 == false)
	{
		if (_root.上装装备 != "")
		{
			_root.物品栏.装备栏.add("上装装备",{name:_root.上装装备, value:{level:1}});
		}
		if (_root.下装装备 != "")
		{
			_root.物品栏.装备栏.add("下装装备",{name:_root.下装装备, value:{level:1}});
		}
		if (_root.脚部装备 != "")
		{
			_root.物品栏.装备栏.add("脚部装备",{name:_root.脚部装备, value:{level:1}});
		}
		if (_root.难度 == "平衡模式（困难）")
		{
			_root.difficultyMode = 0;
		}
		else if (_root.难度 == "逆天模式（简单）")
		{
			_root.difficultyMode = 1;
		}
		else if (_root.难度 == "挑战模式（自限）")
		{
			_root.difficultyMode = 2;
		}
		else
		{
			_root.difficultyMode = 0;
		}
		_root.新建角色();
		this.确认按钮是否失效 = true;
	}
}
on (press) {
	this.gotoAndStop(3);
}
on (rollOver) {
	this.gotoAndStop(2);
}
on (rollOut) {
	this.gotoAndStop(1);
}

