	this._visible = 1;
	if (_root.解析DLC是否可用() == true)
	{
		this._visible = 1;
	}
	if (_root.isChallengeMode() == true)
	{
		this._visible = 0;
	}
}

on (press) {
	if (!_root.暂停)
	{
		_root.暂停 = true;
		_root.发布消息(_root.获得翻译("游戏暂停"));
	}
	else
	{
		_root.暂停 = false;
		_root.发布消息(_root.获得翻译("游戏取消暂停"));
	}
}
onClipEvent (enterFrame) {
	if (_root.暂停)
	{
		this.gotoAndStop(2);
	}
	else
	{
		this.gotoAndStop(1);
	}
}

