	_root.奖励物品界面.奖励品 = [["压缩胶囊之61套士兵",1],["压缩胶囊之硬化僵尸",1],["抗生素",2],["加强抗生素药剂",3],["材料盒子",4],["复活币",5]];
	_root.奖励物品界面.在线奖励刷新(_root.获得翻译("领取5号物资包"));
	this._visible = 0;
}
onClipEvent(load){
   显示文字 = _root.获得翻译("领取5号物资包");
   this._visible = 0;
}
on(rollOver){
   _root.注释(150,_root.获得翻译(this.显示文字));
}
on(rollOut){
   _root.注释结束();
}

onClipEvent (load) {
	_root.加载共享场景(_root.关卡标志);
}
onClipEvent (enterFrame) {
	_root.横版卷屏();
}

onClipEvent (load) {
	_root.加载共享场景("wuxianguotu_1");
	_root.开启生存模式();
}
onClipEvent (enterFrame) {
	_root.横版卷屏();
}
onClipEvent (unload) {
	_root.生存模式关闭();
	_root.无限过图模式 = false;
}

onClipEvent (load) {
	_root.加载共享场景("基地场景-" + _root.关卡标志);
}
onClipEvent (enterFrame) {
	_root.横版卷屏();
}

onClipEvent (load) {
	_root.加载共享场景(_root.关卡标志);
}
onClipEvent (enterFrame) {
	_root.横版卷屏();
}
