Actionscript.script:
function 显示指引(指引帧, 中心x)
{
	this._visible = true;
	gotoAndStop(指引帧);
	if (isNaN(中心x))
	{
		this._alpha = 0;
		this.count = -50;
		this.onEnterFrame = function()
		{
			this.count += 1;
			if (this.count > 0)
			{
				this._alpha = this.count;
			}
			if (this._alpha >= 70)
			{
				delete this.onEnterFrame;
			}
		};
	}
	else
	{
		this.onEnterFrame = function()
		{
			var playerx = _root.gameworld.玩家0._x;
			if (Math.abs(playerx - 中心x) <= 100)
			{
				this._alpha = 75;
			}
			else
			{
				var a = 100 - Math.abs(playerx - 中心x) * 0.25;
				a = a <= 0 ? 0 : a;
				this._alpha = a;
			}
		};
	}
}

function 关闭指引()
{
	this._alpha = 100;
	delete this.onEnterFrame;
	this._visible = false;
}

stop();

on(release){
   显示指引("移动与攻击");
}
