Actionscript.script:
stop();
var listenerObject = new Object();
listenerObject.complete = function(eventObject)
{
	gotoAndStop(2);
};
动画.addEventListener("complete",listenerObject);

on (press) {
	_root.注释结束();
	_parent.gotoAndStop(2);
}
onClipEvent (load) {
	显示文字 = "点击关闭动画";
	_root.全部翻译(this);
}
on (rollOver) {
	_root.注释(150,_root.获得翻译(显示文字));
}
on (rollOut) {
	_root.注释结束();
}
