function 排列佣兵图标()
{
	删除佣兵图标();
	删除可雇用佣兵图标();
	gotoAndStop("佣兵管理");
	var _loc3_ = 0;
	var 同伴最大数量 = Math.max(_root.同伴数, _root.同伴数据.length);
	//while (_loc3_ < _root.同伴数)
	while (_loc3_ < 同伴最大数量)
	{
		if (_root.同伴数据[_loc3_][0] != undefined)
		{
			this.attachMovie("佣兵信息显示框","佣兵信息显示框" + _loc3_,this.getNextHighestDepth(),{_x:8, _y:31 + _loc3_ * 70, 同伴数据数组号:_loc3_, 佣兵数据:_root.同伴数据[_loc3_], 是否出战:_root.佣兵是否出战信息[_loc3_], 名字:_root.同伴数据[_loc3_][1], 等级:_root.同伴数据[_loc3_][0]});

			var 计算强化度 = _root.主角函数.获取人形怪强化等级(_root.同伴数据[_loc3_][0], _root.同伴数据[_loc3_][1]);
			if (isNaN(计算强化度))
			{
				计算强化度 = 1;
			}
			var _loc4_ = 6;
			while (_loc4_ < 17)
			{
				当前强化度 = 计算强化度;
				if(_root.同伴数据[_loc3_][19].装备强化度){
					当前强化度 = _root.同伴数据[_loc3_][19].装备强化度[_root.佣兵装备位映射数组[_loc4_]];
				}
				var 当前物品名 = _root.同伴数据[_loc3_][_loc4_];
				if (当前物品名 != "" and 当前物品名 != undefined)
				{
					var itemData = _root.getItemData(当前物品名);
					this["佣兵信息显示框" + _loc3_].attachMovie("图标-" + itemData.icon,"物品图标" + _loc4_,this["佣兵信息显示框" + _loc3_].getNextHighestDepth(),{_x:(_loc4_ - 6) * 28 + 15.5, _y:44, 物品名:当前物品名, 数量:1, 强化度:当前强化度, 图标:"图标-" + itemData.icon});
					this["佣兵信息显示框" + _loc3_]["物品图标" + _loc4_].onRollOver = function()
					{
						_root.物品图标注释(this.物品名,{level:this.强化度});
					};
					this["佣兵信息显示框" + _loc3_]["物品图标" + _loc4_].onRollOut = function()
					{
						_root.注释结束();
					};
				}
				_loc4_ += 1;
			}
		}
		_loc3_ += 1;
	}
}
function 删除佣兵图标()
{
	var _loc2_ = 0;
	while (_loc2_ < 10)
	{
		this["佣兵信息显示框" + _loc2_].removeMovieClip();
		_loc2_ += 1;
	}
}
function 读取佣兵列表信息(页数)
{
	总页数 = Math.ceil((_root.可雇佣兵.length - _root.隐藏的可雇佣兵.length) / 5);
	可雇用佣兵列表数据 = [];
	var i = 5 * Number(页数) - 5;
	var j = 0;
	var 隐藏佣兵数 = 0;
	while (i < _root.可雇佣兵.length && j < 5)
	{
		if(!_root.可雇佣兵[i][19].隐藏){
			可雇用佣兵列表数据[j] = _root.可雇佣兵[i];
			j++;
		}
		i++;
	}
	排列可雇用佣兵图标(页数);
}
function 排列可雇用佣兵图标(页数值)
{
	gotoAndStop("查找佣兵");
	删除佣兵图标();
	删除可雇用佣兵图标();
	当前页数 = Number(页数值);
	页数显示 = 当前页数 + " / " + 总页数;
	//下一页按钮._visible = 0;
	//上一页按钮._visible = 0;
	下一页._visible = 0;
	下十页._visible = 0;
	上一页._visible = 0;
	上十页._visible = 0;
	if (当前页数 < 总页数)
	{
		下一页._visible = 1;
		下十页._visible = 1;
	}
	if (当前页数 > 1)
	{
		上一页._visible = 1;
		上十页._visible = 1;
	}
	var _loc4_ = 0;
	//var _loc4_ = 5 * 当前页数 - 5;
	//var 当前位数 = 0;
	while (_loc4_ < 5)
	{
		if (可雇用佣兵列表数据[_loc4_][1] != undefined)
		{
			if(可雇用佣兵列表数据[_loc4_][19].隐藏){
				_loc4_ += 1;
				continue;
			}
			var 当前佣兵等级 = Number(可雇用佣兵列表数据[_loc4_][0]);

			var 所需金币 = 可雇用佣兵列表数据[_loc4_][18] * 1.5;
			var 所需K点 = _root.isEasyMode() ? 0 : 可雇用佣兵列表数据[_loc4_][0] >= 50 ? (可雇用佣兵列表数据[_loc4_][0] - 50) * 100 : 0;
			if(可雇用佣兵列表数据[_loc4_][19].价格倍率){
				所需金币 = Math.floor(所需金币 * 可雇用佣兵列表数据[_loc4_][19].价格倍率/500) * 500;
				所需K点 = Math.floor(所需K点 * 可雇用佣兵列表数据[_loc4_][19].价格倍率/100) * 100;
			}
			this.attachMovie("佣兵列表信息显示框","佣兵列表信息显示框" + _loc4_,this.getNextHighestDepth(),{_x:8, _y:31 + _loc4_ * 60, 可雇用佣兵列表数据:可雇用佣兵列表数据[_loc4_], 名字:可雇用佣兵列表数据[_loc4_][1], 等级:可雇用佣兵列表数据[_loc4_][0], 身价:所需K点, 金币身价:所需金币, 热度:可雇用佣兵列表数据[_loc4_][18], 鲜度:0});

			var 计算强化度 = _root.主角函数.获取人形怪强化等级(当前佣兵等级, 可雇用佣兵列表数据[_loc4_][1]);
			if (isNaN(计算强化度))
			{
				计算强化度 = 1;
			}
			var _loc5_ = 6;
			while (_loc5_ < 17)
			{
				当前强化度 = 计算强化度;
				if(可雇用佣兵列表数据[_loc4_][19].装备强化度){
					当前强化度 = 可雇用佣兵列表数据[_loc4_][19].装备强化度[_root.佣兵装备位映射数组[_loc5_]];
				}
				if (可雇用佣兵列表数据[_loc4_][_loc5_] != "" and 可雇用佣兵列表数据[_loc4_][_loc5_] != undefined)
				{
					this["佣兵列表信息显示框" + _loc4_].attachMovie("图标-" + _root.getItemData(可雇用佣兵列表数据[_loc4_][_loc5_]).icon,"物品图标" + _loc5_,this["佣兵列表信息显示框" + _loc4_].getNextHighestDepth(),{_x:(_loc5_ - 6) * 28 + 15.5, _y:44, 物品名:可雇用佣兵列表数据[_loc4_][_loc5_], 数量:1, 强化度:当前强化度, 图标:"图标-" + _root.getItemData(可雇用佣兵列表数据[_loc4_][_loc5_]).icon});
					this["佣兵列表信息显示框" + _loc4_]["物品图标" + _loc5_].onRollOver = function()
					{
						var temp_name = this.物品名 == "角斗高手项链" ? "战斗专家军牌" : this.物品名 ==  "角斗王者项链" ? "战斗狂人军牌" : this.物品名;
						//_root.物品图标注释(this.物品名,{level:this.强化度});
						_root.物品图标注释(temp_name,{level:this.强化度});
					};
					this["佣兵列表信息显示框" + _loc4_]["物品图标" + _loc5_].onRollOut = function()
					{
						_root.注释结束();
					};
				}
				_loc5_ += 1;
			}
		}
		_loc4_ += 1;
		//当前位数++;
	}
}
