function 佣兵报名()
{
	k点价格 = 10;
	if (_root.虚拟币 >= k点价格)
	{
		var _loc2_ = "http://" + _root.address + "/crazyflashercom/k5_ybenroll.action?k=" + random(100);
		var lv = new LoadVars();
		lv.accId = _root.accId;
		lv.moneyvip = k点价格;
		lv.fkey = CF7_PAY_UTL.md5(_root.accId + "&" + lv.moneyvip + "ANZJFGJJ@8*2!^%@&^$@&(&(..)@&#JFJxx");
		lv.sendAndLoad(_loc2_,lv,"POST");
		lv.onLoad = function(b)
		{
			if (b)
			{
				_root.佣兵信息界面.加载图标._visible = 0;
				_root.佣兵信息界面.报名按钮._visible = 1;
				if (lv.content + "" == "-1")
				{
					_root.发布消息("报名失败,请重新报名");
				}
				else if (lv.content + "" == "-2")
				{
					_root.发布消息("您已经报名,无需重复报名");
				}
				else if (lv.content + "" == "-3")
				{
					_root.发布消息("数据异常,不能报名");
				}
				else if (lv.content + "" == "-4")
				{
					_root.发布消息("价格异常,不能报名");
				}
				else if (lv.content + "" == "-5")
				{
					_root.发布消息("k点不足,请先充值");
				}
				else if (lv.content + "" == "-6")
				{
					_root.发布消息("数据有误,报名失败");
				}
				else if (lv.content + "" == "-7")
				{
					_root.发布消息("角色等级不足，请10级再来。");
				}
				else if (lv.content + "" == "0")
				{
					_root.发布消息("恭喜您报名成功");
					_root.最上层发布文字提示("恭喜您报名成功");
					_root.获取虚拟币值();
				}
				else
				{
					_root.发布消息("数据有误,请重新报名");
				}
			}
		};
	}
	else
	{
		_root.佣兵信息界面.加载图标._visible = 0;
		_root.佣兵信息界面.报名按钮._visible = 1;
		_root.发布消息("k点不足,请先充值");
	}
}
function 佣兵赚取K点记录()
{
	var _loc2_ = "http://" + _root.address + "/crazyflashercom/k5_yblog.action?k=" + random(100);
	var lv = new LoadVars();
	lv.accId = _root.accId;
	lv.sendAndLoad(_loc2_,lv,"POST");
	lv.onLoad = function(b)
	{
		if (b)
		{
			if (lv.content + "" == "-1")
			{
				_root.发布消息("报名失败,请重新报名");
			}
			else if (lv.content + "" == "-2")
			{
				被雇佣日志 = "暂时还没有人雇佣你！";
			}
			else if (lv.content + "" == "-3")
			{
				被雇佣日志 = "你还没有报名！快报名赚取k点吧！";
			}
			else
			{
				var _loc3_ = lv.content.split("_");
				var _loc4_ = _loc3_[_loc3_.length - 1].split(",");
				被雇佣日志 = "您一共被雇佣了" + (_loc3_.length - 1) + "次，一共赚取了佣金" + _loc4_[0] + "k点！\r";
				var _loc5_ = 0;
				var _loc6_ = _loc3_.length - 1;
				while (_loc5_ < _loc6_)
				{
					var _loc7_ = _loc3_[_loc5_].split(",");
					被雇佣日志 += _loc7_[0] + " 雇佣了你，获得佣金" + _loc7_[1] + "k点\r";
					_loc5_ += 1;
				}
			}
		}
	};
}
function 佣兵删除记录(佣兵Id)
{
	var _loc3_ = "http://" + _root.address + "/crazyflashercom/k5_ybdeletelog.action?k=" + random(100);
	var lv = new LoadVars();
	lv.accId = _root.accId;
	lv.ybid = 佣兵Id;
	lv.fkey = CF7_PAY_UTL.md5(lv.accId + "&" + lv.ybid + "ANZJFGJJ@8*2!^%@&^$@&(&(..)@&#JFJxx");
	lv.sendAndLoad(_loc3_,lv,"POST");
	lv.onLoad = function(b)
	{
		if (b)
		{
			if (lv.content + "" == "-1")
			{
				_root.发布消息("删除记录失败！");
			}
			else if (lv.content + "" == "-6")
			{
				_root.发布消息("数据异常，删除记录失败！");
			}
			else if (lv.content + "" == "0")
			{
				_root.发布消息("佣兵删除记录成功！");
			}
			else
			{
				_root.发布消息("删除记录失败2！");
			}
		}
	};
}
function 取消佣兵报名()
{
	var _loc2_ = "http://" + _root.address + "/crazyflashercom/k5_ybclose.action?k=" + random(100);
	var lv = new LoadVars();
	lv.accId = _root.accId;
	lv.fkey = CF7_PAY_UTL.md5(_root.accId + "ANZJFGJJ@8*2!^%@&^$@&(&(..)@&#JFJxx");
	lv.sendAndLoad(_loc2_,lv,"POST");
	lv.onLoad = function(b)
	{
		if (b)
		{
			if (lv.content + "" == "-1")
			{
				_root.发布消息("取消失败");
			}
			else if (lv.content + "" == "-2")
			{
				_root.发布消息("没有报名");
			}
			else if (lv.content + "" == "-3")
			{
				_root.发布消息("验证失败");
			}
			else if (lv.content + "" == "0")
			{
				_root.发布消息("佣兵报名取消成功！");
			}
			else
			{
				_root.发布消息("取消失败");
			}
		}
	};
}
