Actionscript.script:
onClipEvent (load) {
	stop();
}
on (release) {
	_root.决斗场关闭();
	_root.淡出动画.淡出跳转帧("关卡地图");
}
on (press) {
	this.gotoAndStop(3);
}
on (rollOver) {
	this.gotoAndStop(2);
}
on (rollOut) {
	this.gotoAndStop(1);
}

onClipEvent(load){
   敌人等级下限 = 1;
   敌人等级上限 = 5;
   敌人同伴数 = 1;
   简介 = _root.获得翻译("挑战对手数量：") + 敌人同伴数 + "  " + _root.获得翻译("对手等级：") + 敌人等级下限 + "—" + 敌人等级上限 + "";
   当前关卡名 = "DEATH MATCH角斗场";
   多语言当前关卡名 = _root.获得翻译("DEATH MATCH角斗场");
   奖金 = 敌人同伴数 * 敌人等级下限 * 1000;
   淡出跳转帧 = "角斗场-2";
   关卡开放条件 = 1;
   敌人同伴数据 = [];
   i = 0;
   while(i < 敌人同伴数)
   {
      敌人同伴数据[i] = [];
      i++;
   }
}

