function 获得训练项目文本()
{
	训练数组 = _root.健身房训练类型;
	for (i = 0; i < 13; i++)
	{
		i1 = i + 1;
		if (训练数组[i] && 训练数组[i].属性名)
		{
			if (训练数组[i].消耗 == 0)
			{
				消耗字符 = "免费";
			}
			else
			{
				消耗字符 = 训练数组[i].消耗 + (训练数组[i].货币名 == "金钱" ? "金币" : 训练数组[i].货币名);
			}
			this["text" + i1].text = 消耗字符 + "训练" + Math.floor(训练数组[i].时长 / 1000) + "秒提升" + 训练数组[i].加成 + 训练数组[i].加成名;
		}else{
			this["text" + i1].text = "";
		}
	}
}
获得训练项目文本();
