on (press) {
	var 训练参数 = _root.健身房训练类型[12];
	if (训练参数 && 训练参数.属性名 == "技能点")
	{
		_root.gameworld.挂机计时器.虚拟币支付2(训练参数.消耗,"O20111009000000013",训练参数.时长,训练参数.加成,挂机项目);
	}
	this._visible = 0;
}
on (rollOver) {
	var 训练参数 = _root.健身房训练类型[12];
	if (训练参数 && 训练参数.属性名 == "技能点")
	{
		_root.注释(100,_root.获得翻译("获得"+训练参数.属性名 +"：") + 训练参数.加成 + "，" + _root.获得翻译("需要秒数：") + Math.floor(训练参数.时长/1000) + "，" + _root.获得翻译("K点价格：") + 训练参数.消耗);
	}
}
on (rollOut) {
	_root.注释结束();
}

