		_root.注释(100,_root.获得翻译(训练参数.加成名 + "永久提升：") + 训练参数.加成 + "，" + _root.获得翻译("需要秒数：") + Math.floor(训练参数.时长/1000) + "，" +训练参数.货币名 + _root.获得翻译("价格：") + 训练参数.消耗);
	}
}
on (rollOut) {
	_root.注释结束();
}
