on (press) {
	var 训练参数 = _root.健身房训练类型[2];
	if(!训练参数.属性名){
		_root.注释结束();
	}
	else if (_root[训练参数.货币名] >= 训练参数.消耗)
	{
		if (训练参数.货币名 == "金钱")
		{
			_root.gameworld.挂机计时器.挂机项目 = 挂机项目;
			_root.gameworld[挂机项目].开始挂机();
			_root.gameworld.挂机计时器.计时初始化(训练参数.时长,10000);
		}
		else if (训练参数.货币名 == "K点")
		{
			_root.gameworld.挂机计时器.虚拟币支付(训练参数.消耗,"O20111009000000007",训练参数.时长,10000,挂机项目);
		}
		this._visible = 0;
		setTimeout(function ()
		{
			if (_root[训练参数.属性名] <= 训练参数.上限)
			{
				_root[训练参数.属性名] += 训练参数.加成;
				if (_root[训练参数.属性名] > 训练参数.上限)
				{
					_root[训练参数.属性名] = 训练参数.上限;
				}
				if (训练参数.货币名 == "金钱")
				{
					_root[训练参数.货币名] -= 训练参数.消耗;
					_root.播放音效("收银机.mp3");
				}
				_root.发布消息("当前的" + 训练参数.加成名 + "健身加成为" + _root[训练参数.属性名]);
				_root.发布消息(训练参数.加成名 + "加成到达" + 训练参数.上限 + "上限时，锻炼成果将转为经验值");
			}
			else
			{
				_root.经验值 += 50000;
				_root.发布消息("通过锻炼获得的" + 训练参数.加成名 + "加成达到上限，锻炼成果转为50000点经验值");
			}
		},训练参数.时长);
	}
	else
	{
		_root.发布消息(训练参数.货币名 + "不足！");
	}
}
on (rollOver) {
	var 训练参数 = _root.健身房训练类型[2];
	if(训练参数.属性名){
		_root.注释(100,_root.获得翻译(训练参数.加成名 + "永久提升：") + 训练参数.加成 + "，" + _root.获得翻译("需要秒数：") + Math.floor(训练参数.时长/1000) + "，" +训练参数.货币名 + _root.获得翻译("价格：") + 训练参数.消耗);
	}
}
on (rollOut) {
	_root.注释结束();
}
