Actionscript.script:
function 刷新()
{
   删除奖励块();
   排列奖励块();
   if(奖励品.length >= 1)
   {
      this._visible = 1;
   }
}
function 在线奖励刷新(值)
{
   this.标题 = 值;
   删除奖励块();
   排列奖励块();
   if(奖励品.length > 0)
   {
      this._visible = 1;
   }
}
function 排列奖励块()
{
   var _loc3_ = 0;
   var _loc4_ = 0;
   while(_loc4_ < 奖励品.length)
   {
      if(奖励品[_loc4_][0] != undefined)
      {
         this.attachMovie("奖励物品-奖励物品显示块","奖励块" + _loc4_,this.getNextHighestDepth(),{_x:8,_y:_loc3_ * 32 + 30,物品名:奖励品[_loc4_][0],多语言物品名:_root.getItemData(奖励品[_loc4_][0]).displayname,数量:奖励品[_loc4_][1],数组id:_loc4_});
         _loc3_ += 1;
      }
      _loc4_ += 1;
   }
}
function 删除奖励块()
{
   i = 0;
   while(i <= 100)
   {
      this["奖励块" + i].removeMovieClip();
      i++;
   }
}
function 生成关卡随机奖励品()
{
   奖励品 = [];
   var _loc2_ = 0;
   while(_loc2_ < _root.关卡可获得奖励品.length)
   {
      if(_root.是否是某网站(["andylaw.net","www.andylaw.net","game.andylaw.net","crazyparkour.andylaw.net"]) == true)
      {
         _root.画面效果("AndyLaw.net特殊效果",Stage.width / 2,Stage.height / 2,100);
         if(random(Math.floor(_root.关卡可获得奖励品[_loc2_][1]) / 2) == 0)
         {
            奖励品.push([_root.关卡可获得奖励品[_loc2_][0],random(_root.关卡可获得奖励品[_loc2_][2]) + 1]);
         }
      }
      else if(random(_root.关卡可获得奖励品[_loc2_][1]) == 0)
      {
         奖励品.push([_root.关卡可获得奖励品[_loc2_][0],random(_root.关卡可获得奖励品[_loc2_][2]) + 1]);
      }
      _loc2_ += 1;
   }
}
function 一键领取全部()
{
   var 已领取数量 = 0;
   var 领取失败 = false;
   var i = 0;
   while(i < 奖励品.length)
   {
      if(奖励品[i][0] == undefined)
      {
         i++;
         continue;
      }
      var 物品名 = 奖励品[i][0];
      var 数量 = 奖励品[i][1];
      if(物品名 == "经验")
      {
         _root.经验值 += 数量;
         奖励品[i] = [];
         已领取数量++;
      }
      else if(物品名 == "金钱")
      {
         _root.金钱 += 数量;
         奖励品[i] = [];
         已领取数量++;
      }
      else if(_root.itemAcquire([{name:物品名, value:数量}]))
      {
         _root.发布消息("获得:" + _root.getItemData(物品名).displayname + " X " + 数量);
         奖励品[i] = [];
         已领取数量++;
      }
      else
      {
         领取失败 = true;
         break;
      }
      i++;
   }
   if(已领取数量 > 0)
   {
      _root.播放音效("ammopickup1.wav");
   }
   if(领取失败)
   {
      _root.发布消息(_root.获得翻译("背包已满，剩余物品无法领取！"));
      刷新();
   }
   else
   {
      return true;
   }
   return false;
}
_root.全部翻译(this);

onClipEvent(load){
   物品名 = "抗生素";
   数量 = 5;
   this._visible = 0;
}

on (release) {
	if (_root.物品栏界面._visible)
	{
		_root.储存数据库存盘();
	}
	_root.保存仓库数据();
	_root.自动存盘();
	_root.存储金钱界面._visible = 0;
	this._visible = 0;
	_root.是否达成任务检测();
}

on(release){
   stopDrag();
}
on(press){
   startDrag(this,0);
   _root.鼠标.gotoAndStop("手型抓取");
}
on(rollOver){
   _root.鼠标.gotoAndStop("手型准备抓取");
}
on(rollOut){
   _root.鼠标.gotoAndStop("手型普通");
}

onClipEvent (load) {
	stop();
	_root.全部翻译(this);
}
on (release) {
	gotoAndStop(1);
	if (_parent.一键领取全部())
	{
		if (_root.物品栏界面._visible)
		{
			_root.储存数据库存盘();
		}
		_root.保存仓库数据();
		_root.自动存盘();
		_root.存储金钱界面._visible = 0;
		_parent._visible = 0;
		_root.是否达成任务检测();
	}
}
on (press) {
	gotoAndStop(3);
}
on (rollOver) {
	gotoAndStop(2);
}
on (rollOut) {
	gotoAndStop(1);
}
