Actionscript.script:
function 排列宠物图标()
{
	删除宠物图标();
	temp_xmove = 15;
	temp_ymove = 30;
	temp_height = 63;
	temp_width = 355;
	temp_l = 0;
	temp_j = 1;
	temp_xlimit = 2;
	temp_ylimit = 5;
	temp_a = [];
	var _loc3_ = (页数 - 1) * temp_ylimit * temp_xlimit;
	while (_loc3_ < 页数 * temp_j * temp_ylimit * temp_xlimit)
	{
		if (_loc3_ < _root.最大宠物格子数 and _loc3_ % 2 == 0)
		{
			this.attachMovie("宠物信息显示框","宠物信息显示框" + _loc3_,this.getNextHighestDepth(),{宠物信息数组号:_loc3_, 宠物库数组号:_root.宠物信息[_loc3_][0], 宠物名:_root.宠物库[_root.宠物信息[_loc3_][0]].Name, 宠物等级:_root.宠物信息[_loc3_][1], 宠物体力:_root.宠物信息[_loc3_][2], 是否出战:_root.宠物信息[_loc3_][4]});
			temp_a[_loc3_] = this["宠物信息显示框" + _loc3_];
			temp_a[_loc3_]._x = temp_xmove;
			temp_a[_loc3_]._y = temp_ymove;
			temp_xmove += temp_l;
			if (_root.宠物信息[_loc3_][5].基础训练 && _root.宠物信息[_loc3_][5].基础训练.次数)
			{
				var 后缀 =  _root.战宠进阶函数.基础训练.罗马数字[_root.宠物信息[_loc3_][5].基础训练.次数-1];
				后缀 = 后缀 ? 后缀 : "";
				temp_a[_loc3_].宠物名 = _root.宠物库[_root.宠物信息[_loc3_][0]].Name + 后缀;
			}
			if (_loc3_ % temp_j == 0)
			{
				temp_xmove += temp_width;
			}
			if (_loc3_ < _root.宠物领养限制 and this["宠物信息显示框" + _loc3_].宠物名 == undefined)
			{
				this["宠物信息显示框" + _loc3_].gotoAndStop(2);
			}
			else if (_loc3_ >= _root.宠物领养限制)
			{
				this["宠物信息显示框" + _loc3_].gotoAndStop(3);
			}
		}
		else
		{
			this.attachMovie("宠物信息显示框","宠物信息显示框" + _loc3_,this.getNextHighestDepth(),{宠物信息数组号:_loc3_, 宠物库数组号:_root.宠物信息[_loc3_][0], 宠物名:_root.宠物库[_root.宠物信息[_loc3_][0]].Name, 宠物等级:_root.宠物信息[_loc3_][1], 宠物体力:_root.宠物信息[_loc3_][2], 是否出战:_root.宠物信息[_loc3_][4]});
			temp_a[_loc3_] = this["宠物信息显示框" + _loc3_];
			temp_a[_loc3_]._x = temp_xmove;
			temp_a[_loc3_]._y = temp_ymove;
			temp_xmove += temp_l;
			if (_root.宠物信息[_loc3_][5].基础训练 && _root.宠物信息[_loc3_][5].基础训练.次数)
			{
				var 后缀 =  _root.战宠进阶函数.基础训练.罗马数字[_root.宠物信息[_loc3_][5].基础训练.次数-1];
				后缀 = 后缀 ? 后缀 : "";
				temp_a[_loc3_].宠物名 = _root.宠物库[_root.宠物信息[_loc3_][0]].Name + 后缀;
			}
			if (_loc3_ % temp_j == 0)
			{
				temp_ymove += temp_height;
			}
			if (_loc3_ < _root.宠物领养限制 and this["宠物信息显示框" + _loc3_].宠物名 == undefined)
			{
				this["宠物信息显示框" + _loc3_].gotoAndStop(2);
			}
			else if (_loc3_ >= _root.宠物领养限制)
			{
				this["宠物信息显示框" + _loc3_].gotoAndStop(3);
			}
			temp_xmove = 15;
		}
		_loc3_ = _loc3_ + 1;
	}
}
function 删除宠物图标()
{
	var _loc2_ = 0;
	while (_loc2_ < 40)
	{
		this["宠物信息显示框" + _loc2_].removeMovieClip();
		_loc2_ = _loc2_ + 1;
	}
	_root.宠物信息界面.宠物进阶页.removeMovieClip();
}
页数 = 1;
排列宠物图标();
stop();
_root.全部翻译(this);
this._visible = false;

on(release){
   stopDrag();
}
on(press){
   startDrag(this,0);
   _root.鼠标.gotoAndStop("手型抓取");
}
on(rollOver){
   _root.鼠标.gotoAndStop("手型准备抓取");
}
on(rollOut){
   _root.鼠标.gotoAndStop("手型普通");
}

on(release){
   _parent.页数 = _parent.页数 + 1;
   _parent.排列宠物图标();
   if(_parent.页数 == 4)
   {
      this._visible = 0;
   }
   if(_parent.页数 > 1)
   {
      _parent.上一页._visible = 1;
   }
}
onClipEvent(load){
   显示文字 = _root.获得翻译("下一页");
}

on(release){
   _parent.页数--;
   _parent.排列宠物图标();
   if(_parent.页数 == 1)
   {
      this._visible = 0;
   }
   if(_parent.页数 < 4)
   {
      _parent.下一页._visible = 1;
   }
}
onClipEvent(load){
   显示文字 = _root.获得翻译("上一页");
   if(_parent.页数 == 1)
   {
      this._visible = 0;
   }
}
