Actionscript.script:
function 购买宠物(该宠物信息, 格子位置号)
{
	_root.删除场景宠物();
	_root.宠物信息[格子位置号] = 该宠物信息;
	_root.最大宠物出战数 = Math.min(_root.等级 / 5, 5);
	if (_root.isChallengeMode() == true)
	{
		_root.最大宠物出战数 = _root.等级 / 35;
	}
	if (_root.isEasyMode() == true)
	{
		_root.最大宠物出战数 = 5 + _root.等级 / 5;
	}
	if (_root.出战宠物id库.length >= _root.最大宠物出战数)
	{
		_root.宠物信息[格子位置号][4] = 0;
	}
	_root.宠物信息[格子位置号].push(0);
	_root.宠物信息界面.aaa = _root.宠物信息;
	_root.宠物信息界面.排列宠物图标();
	_root.加载宠物(_root.gameworld[_root.控制目标]._x,_root.gameworld[_root.控制目标]._y);
}
function 是否有空格子()
{
	var _loc2_ = 0;
	while (_loc2_ < _root.宠物信息.length)
	{
		if (_root.宠物信息[_loc2_].length == 0)
		{
			return true;
		}
		_loc2_ += 1;
	}
	return false;
}
function 虚拟币支付(amount, 商品编号, 该宠物信息, 折扣值)
{
	if (_root.虚拟币 >= amount * 折扣值)
	{
		if (是否有空格子() == true)
		{
			_root.虚拟币 -= amount * 折扣值;
			_root.最上层发布文字提示(_root.获得翻译("支付成功！"));
			_root.联机2015发送传言("K点消费");
			text = "支付成功！";
			_root.播放音效("收银机.mp3");
			购买宠物(该宠物信息,格子位置号);
			_root.获取虚拟币值();
			gotoAndStop("成功");
		}
		else
		{
			_root.最上层发布文字提示(_root.获得翻译("超出数量限制"));
		}
	}
	else
	{
		_root.最上层发布文字提示(_root.获得翻译("K点不足！"));
		text = _root.获得翻译("K点不足！");
	}
}
function 金币支付(amount, 商品编号, 该宠物信息)
{
	if (_root.金钱 >= amount)
	{
		if (是否有空格子() == true)
		{
			_root.最上层发布文字提示(_root.获得翻译("支付成功！"));
			text = _root.获得翻译("支付成功！");
			_root.播放音效("收银机.mp3");
			gotoAndStop("成功");
			_root.最上层发布文字提示(_root.获得翻译("支付成功！"));
			text = _root.获得翻译("购买成功！");
			_root.播放音效("收银机.mp3");
			_root.金钱 -= amount;
			购买宠物(该宠物信息,格子位置号);
			gotoAndStop("成功");
		}
		else
		{
			_root.最上层发布文字提示(_root.获得翻译("超出数量限制"));
		}
	}
	else
	{
		_root.最上层发布文字提示(_root.获得翻译("金钱不足！"));
		text = _root.获得翻译("金钱不足！");
	}
}
PetPage = 1;//折扣值 = _root.战宠折扣;
折扣值 = 1;
是否等级限制 = _root.运营活动战宠等级限制;

if (_root.最强战宠是否显示 != 1)
{
	if (_root.最强战宠是否显示 == 0)
	{
		gotoAndStop(2);
	}
}

function 已有该宠物(宠物序号)
{
	var i = 0;
	while (i < _root.宠物信息.length)
	{
		if (_root.宠物信息[i][0] == 宠物序号)
		{
			return true;
		}
		i++;
	}
	return false;
}

stop();
this._visible = false;

on(release){
   _parent.PetPage += 1;
   _parent.nextFrame();
   if(_parent.PetPage == 6 and _root.主线任务进度 < 71)
   {
      this._visible = 0;
   }
   else
   {
      this._visible = 1;
   }
}
onClipEvent(load){
   显示文字 = _root.获得翻译("下一页");
}

on(release){
   _parent.PetPage += 1;
   _parent.nextFrame();
}
onClipEvent(load){
   显示文字 = _root.获得翻译("下一页");
}

on (release) {
	_parent.prevFrame();
	_parent.PetPage -= 1;
	_parent.下一页._visible = 1;
}
onClipEvent (load) {
	显示文字 = _root.获得翻译("上一页");
}

onClipEvent (load) {
	开放等级 = 1;
	开放任务进度 = 1;
	该宠物信息 = [0, 1, 100, 0, 1];
	注释文字 = _root.宠物库[该宠物信息[0]].Name + "  ";
	显示文字 = _root.获得翻译("K点价格：") + 该宠物信息[3] * _parent.折扣值;
	显示文字 = _root.获得翻译("金币价格：") + 2000;
	注释文字 += 显示文字 + "  ";
	if (_root.等级 < 开放等级)
	{
		注释文字 += _root.获得翻译("解锁等级：") + 开放等级;
	}
	if (_root.主线任务进度 < 开放任务进度)
	{
		注释文字 += _root.获得翻译("主线任务进度需达到") + 开放任务进度;
	}
	if (_root.等级 < 开放等级 or _root.主线任务进度 < 开放任务进度)
	{
		this.gotoAndStop("锁定");
		锁定 = true;
	}
}
on (release) {
	if (锁定 != true)
	{
		_parent.金币支付(2000,"S20130530000018",该宠物信息);
	}
}
on (rollOver) {
	_root.注释(100,注释文字);
}
on (rollOut) {
	_root.注释结束();
}

