   开放任务进度 = 61;
   if(_parent.是否等级限制 == 0)
   {
      开放等级 = 1;
      开放任务进度 = 1;
   }
   该宠物信息 = [11,1,100,1200,1];
   注释文字 = _root.宠物库[该宠物信息[0]][0] + "  ";
   显示文字 = _root.获得翻译("K点价格：") + 该宠物信息[3] * _parent.折扣值;
   显示文字 = _root.获得翻译("金币价格：") + 11000;
   注释文字 += 显示文字 + "  ";
   if(_root.等级 < 开放等级)
   {
      注释文字 += _root.获得翻译("解锁等级：") + 开放等级;
   }
   if(_root.主线任务进度 < 开放任务进度)
   {
      注释文字 += _root.获得翻译("主线任务进度需达到") + 开放任务进度;
   }
   if(_root.等级 < 开放等级 or _root.主线任务进度 < 开放任务进度)
   {
      this.gotoAndStop("锁定");
      锁定 = true;
   }
}
on(release){
   if(锁定 != true)
   {
      _parent.金币支付(11000,"S20130530000018",该宠物信息);
   }
}
on(rollOver){
   if(注释文字 != "")
   {
      _root.注释(100,注释文字);
   }
}
on(rollOut){
   _root.注释结束();
}

