      开放等级 = 1;
      开放任务进度 = 1;
   }
   该宠物信息 = [16,1,100,1000,1];
   注释文字 = _root.宠物库[该宠物信息[0]][0] + "  ";
   显示文字 = 该宠物信息[3] * _parent.折扣值 + "K点 购买";
   注释文字 += 显示文字 + "  ";
   if(_root.等级 < 开放等级)
   {
      注释文字 += "等级达到" + 开放等级 + "级时解锁。";
   }
   if(_root.主线任务进度 < 开放任务进度)
   {
      注释文字 += "主线任务达到" + 开放任务进度 + "时解锁。";
   }
   if(_root.等级 < 开放等级 or _root.主线任务进度 < 开放任务进度)
   {
      this.gotoAndStop("锁定");
      锁定 = true;
   }
}
