onClipEvent(load){
   if(_parent.PetPage == 6 and _root.主线任务进度 < 71)
   {
      this._visible = 1;
   }
   else
   {
      this._visible = 0;
   }
}

on(release){
   this._visible = 0;
}

on(release){
   stopDrag();
}
on(press){
   startDrag(this,0);
   _root.鼠标.gotoAndStop("手型抓取");
}
on(rollOver){
   _root.鼠标.gotoAndStop("手型准备抓取");
}
on(rollOut){
   _root.鼠标.gotoAndStop("手型普通");
}
