Actionscript.script:
stop();
界面 = "物品装备";
_root.全部翻译(this);

on(rollOver){
   _root.注释(100,_root.获得翻译("将物品拖入垃圾箱，永久丢弃物品！无法恢复！"));
}
on(rollOut){
   _root.注释结束();
}

on(release){
   _root.手部装备 = "";
   手部装备.已装备名 = "";
   _root.物品栏[手部装备.对应数组号][2] = 0;
   手部装备.是否装备 = 0;
   手部装备.gotoAndStop(1);
   _root.排列物品图标();
   _root.刷新人物装扮(_root.控制目标);
   _root.client.sendData(DataPackage.换装备("手部装备","null"));
}

