onClipEvent(load){
   是否装备 = 0;
   装备槽类别 = this._name;
   已装备名 = _root[this._name];
   对应装备 = this._name;
   i = 0;
   while(i < _root.物品栏总数)
   {
      if(_root.物品栏[i][0] == 已装备名 and _root.物品栏[i][2] == 1)
      {
         this.对应数组号 = i;
         this.是否装备 = 1;
         break;
      }
      i++;
   }
   if(已装备名 == "空" or 已装备名 == "")
   {
      this.gotoAndStop(已装备名);
   }
   else
   {
      this.图标 = "图标-" + _root.getItemData(已装备名).icon;
      this.gotoAndStop("默认图标");
   }
}

