	//_root.注释(100,_root.获得翻译("点击一键排序，长按自选方式（默认保持紧密原序）"));
}
on (rollOut) {
	_root.注释结束();
}

onClipEvent (load) {
	// 初始化长按参数
	var pressFrames = 0;
	var isPressing = false;
	var checkLongPress = false;// 控制enterFrame检测开关

	// 帧数阈值
	var LONG_PRESS_FRAMES = 30;

	// 定义处理函数
	this.resetPressState = function()
	{
		isPressing = false;
		pressFrames = 0;
		checkLongPress = false;
	};

	this.methodName = "default";

	this.clickAction = function()
	{
		_root.帧计时器.eventBus.publish("物品栏排序图标点击", this.methodName);
	};

	this.longPressAction = function()
	{
		_root.帧计时器.eventBus.publish("物品栏排序图标长按", this.methodName);
	};
}

onClipEvent (mouseDown) {
	if (this.area.hitTest(_root._xmouse, _root._ymouse, true))
	{
		isPressing = true;
		pressFrames = 0;
		checkLongPress = true;// 激活enterFrame检测
	}
}

onClipEvent (mouseUp) {
	if (isPressing)
	{
		// 短按触发判断
		if (pressFrames < LONG_PRESS_FRAMES)
		{
			this.clickAction();
		}
		this.resetPressState();
	}
}

onClipEvent (enterFrame) {
	if (!checkLongPress)
	{
		return;
	}
	// 非检测状态直接退出 
	if (isPressing)
	{
		// 持续区域检测
		if (!this.area.hitTest(_root._xmouse, _root._ymouse, true))
		{
			this.resetPressState();
			return;
		}
		// 帧数累加 
		pressFrames++;

		// 长按触发
		if (pressFrames >= LONG_PRESS_FRAMES)
		{
			this.longPressAction();
			this.resetPressState();// 完成后立即关闭检测
		}
	}
}

on (rollOver) {
	if(this.methodNameNumber < 0)
	{
		_root.注释(100,_root.获得翻译("按总价顺序整理"));		
	}
	else
	{
		_root.注释(100,_root.获得翻译("按默认顺序整理"));
	}

	//_root.注释(100,_root.获得翻译("点击一键排序，长按自选方式（默认保持紧密原序）"));
}
on (rollOut) {
	_root.注释结束();
}

onClipEvent (load) {
	// 初始化长按参数
	var pressFrames = 0;
	var isPressing = false;
	var checkLongPress = false;// 控制enterFrame检测开关

	// 帧数阈值
	var LONG_PRESS_FRAMES = 30;

	// 定义处理函数
	this.resetPressState = function()
	{
		isPressing = false;
		pressFrames = 0;
		checkLongPress = false;
	};

	this.methodNameNumber = -1;

	this["methodName" + "-1"] = "default";
	this["methodName" + "1"] = "byPrice";

	this.getMethodName = function()
	{
		this.methodNameNumber *= -1;

		return "methodName" + this.methodNameNumber;
	}

	this.clickAction = function()
	{
		_root.帧计时器.eventBus.publish("材料栏排序图标点击", this[this.getMethodName()]);
	};

	this.longPressAction = function()
	{
		_root.帧计时器.eventBus.publish("材料栏排序图标长按", this[this.getMethodName()]);
	};
}

onClipEvent (mouseDown) {
	if (this.area.hitTest(_root._xmouse, _root._ymouse, true))
	{
		isPressing = true;
		pressFrames = 0;
		checkLongPress = true;// 激活enterFrame检测
	}
}

onClipEvent (mouseUp) {
	if (isPressing)
	{
		// 短按触发判断
		if (pressFrames < LONG_PRESS_FRAMES)
		{
			this.clickAction();
		}
		this.resetPressState();
	}
}

onClipEvent (enterFrame) {
	if (!checkLongPress)
	{
		return;
	}
	// 非检测状态直接退出 
	if (isPressing)
	{
		// 持续区域检测
		if (!this.area.hitTest(_root._xmouse, _root._ymouse, true))
		{
			this.resetPressState();
			return;
		}
		// 帧数累加 
		pressFrames++;

		// 长按触发
		if (pressFrames >= LONG_PRESS_FRAMES)
		{
			this.longPressAction();
			this.resetPressState();// 完成后立即关闭检测
		}
	}
}

