Actionscript.script:
this.技能栏列表 = [null, 快捷技能栏1, 快捷技能栏2, 快捷技能栏3, 快捷技能栏4, 快捷技能栏5, 快捷技能栏6, 快捷技能栏7, 快捷技能栏8, 快捷技能栏9, 快捷技能栏10, 快捷技能栏11, 快捷技能栏12];

function 刷新技能等级显示()
{
	for (var i = 1; i < 13; i++)
	{
		技能栏列表[i].数量 = _root.根据技能名查找主角技能等级(技能栏列表[i].已装备名);
	}
}

function 取消装备技能(编号)
{
	_root["快捷技能栏" + 编号] = "";
	var 技能栏 = this.技能栏列表[编号];
	技能栏.已装备名 = "";
	技能栏.是否装备 = 0;
	技能栏.gotoAndStop(1);
	_root.主角技能表[技能栏.对应数组号][2] = false;
	_root.主角技能表[技能栏.对应数组号][4] = false;
	if (_root.主角技能表[技能栏.对应数组号][3].indexOf("被动") > -1)
	{
		_root.更新主角被动技能();
		_root.gameworld[_root.控制目标].被动技能 = _root.主角被动技能;
	}
	_root.排列技能图标();
}

stop();

on(rollOver){
   _root.注释(150,_root.获得翻译("技能点数") + "：" + _root.技能点数);
}
on(rollOut){
   _root.注释结束();
}

