onClipEvent (load) {
	function 快捷技能栏图标刷新()
	{
		是否装备 = 0;
		装备槽类别 = this._name;
		已装备名 = _root[this._name];
		对应装备 = this._name;
		控制器 = "控制器1";
		进度条 = "进度条1";
		if (已装备名 != "")
		{
			i = 0;
			while (i <= _root.主角技能表.length)
			{
				if (_root.主角技能表[i][0] == 已装备名)
				{
					this.对应数组号 = i;
					this.是否装备 = 1;
					this.数量 = _root.主角技能表[i][1];
					该技能全部属性 = _root.根据技能名查找全部属性(已装备名);
					this.冷却时间 = 该技能全部属性.CD;
					this.消耗mp = 该技能全部属性.MP;
					break;
				}
				i++;
			}
		}
		if (已装备名 == "空" or 已装备名 == "" or 已装备名 == undefined or this.数量 == 0)
		{
			this.gotoAndStop(已装备名);
		}
		else
		{
			this.图标 = "图标-" + 已装备名;
			this.gotoAndStop("默认图标");
		}
	}
	快捷技能栏图标刷新();
}

