Actionscript.script:
//检测全局音量
globalVolBtn._x = 40 + _root.soundEffectManager.getGlobalVolume() * 1.5;
bgmVolBtn._x = 40 + _root.soundEffectManager.getBGMVolume() * 1.5;

function 刷新键值设定显示()
{
   _root.刷新键值设定();
   _root.gameworld[_root.控制目标].gotoAndStop("刷新装扮");
   var _loc3_ = 0;
   while(_loc3_ < _root.键值设定.length)
   {
      this["键名" + _loc3_] = _root.键值设定[_loc3_][0] + "        " + _root.keyshow(_root.键值设定[_loc3_][2]);
      _loc3_ += 1;
   }
   _root.gameworld[_root.控制目标].获取键值();
}
function 更改键设定(键值设定数组号)
{
   _root.发布消息(_root.获得翻译("请输入……"));
   var mykeyListener = new Object();
   mykeyListener.onKeyDown = function()
   {
      mykey = Key.getCode();
      _root.发布消息(_root.获得翻译("您按下的键是") + _root.keyshow(mykey));
   };
   mykeyListener.onKeyUp = function()
   {
      var _loc2_ = 0;
      while(_loc2_ < _root.键值设定.length)
      {
         if(mykey == _root.键值设定[_loc2_][2])
         {
            是否重复 = true;
            break;
         }
         是否重复 = false;
         _loc2_ += 1;
      }
      if(是否重复 == false)
      {
         _root.发布消息(_root.获得翻译("设置成功！"));
         _root.键值设定[键值设定数组号][2] = mykey;
         刷新键值设定显示();
      }
      else
      {
         _root.发布消息(_root.获得翻译("发现键盘设置有重复，设置失败"));
         刷新键值设定显示();
      }
      Key.removeListener(mykeyListener);
   };
   Key.addListener(mykeyListener);
}
stop();
