on(press){
	this.volMC.onEnterFrame = function(){
		var xmouse = this._xmouse;
		var vol = (xmouse - 32.5) / 1.5;
		vol -= vol % 10;
		if(vol <= 0) vol = 0;
		if(vol > 100) vol = 100;
		globalVolBtn._x = vol * 1.5 + 40;
		_root.soundEffectManager.setGlobalVolume(vol);
	}
}
on(release, releaseOutside){
	delete this.volMC.onEnterFrame;
}

