Actionscript.script:
if (!this.击中时触发函数)
{
	this.击中时触发函数 = function()
	{
		if (!this.已爆炸)
		{
			this.已爆炸 = true;
			this.xmov = 0;
			this.ymov = 0;
			this.霰弹值 = 3;
			gotoAndPlay("爆炸");
		}else{
			this.击中时触发函数 = false;
		}
	};
}
this.已爆炸 = false;
this.霰弹值 = 1;

声音 = "";
霰弹值 = 1;
子弹散射度 = 5;
发射效果 = "";
子弹种类 = "小火焰爆炸";
if(!子弹威力)
{
   子弹威力 = 500;
}
else
{
   子弹威力 *= 0.5;
}
子弹速度 = 0;
击中地图效果 = "";
Z轴攻击范围 = 80;
击倒率 = 1;
 击中后子弹的效果 = "";
 击退初速度 = 25;
 shootX = this._x;
 shootY = this._y;

 var 子弹属性 = _root.子弹属性初始化(this,子弹种类,发射者名);
 子弹属性.声音 = 声音;
 子弹属性.霰弹值 = 霰弹值;
 子弹属性.子弹散射度 = 子弹散射度;
 子弹属性.发射效果 = 发射效果;
 子弹属性.子弹种类 = 子弹种类;
 子弹属性.子弹威力 = 子弹威力;
 子弹属性.子弹速度 = 子弹速度;
 子弹属性.Z轴攻击范围 = Z轴攻击范围;
 子弹属性.击中地图效果 = 击中地图效果;
 子弹属性.击倒率 = 击倒率;
 子弹属性.击中后子弹的效果 = 击中后子弹的效果;
 子弹属性.水平击退速度 = 击退初速度;
 子弹属性.shootX = shootX;
 子弹属性.shootY = shootY;
 子弹属性.shootZ = Z轴坐标;
 子弹属性.发射者 = 发射者名;

 // 锁定发射瞬间的属性，避免切换武器/模组后爆炸继承到当前主角属性
 子弹属性.伤害类型 = (this.伤害类型 != undefined) ? this.伤害类型 : "物理";
 子弹属性.魔法伤害属性 = this.魔法伤害属性;
 子弹属性.毒 = this.毒;
 子弹属性.nanoToxic = this.nanoToxic;
 子弹属性.nanoToxicDecay = this.nanoToxicDecay;
 子弹属性.吸血 = this.吸血;
 子弹属性.击溃 = this.击溃;
 子弹属性.斩杀 = this.斩杀;
 子弹属性.暴击 = this.暴击;
 子弹属性.命中率 = this.命中率;
 子弹属性.固伤 = this.固伤;
 子弹属性.百分比伤害 = this.百分比伤害;
 子弹属性.血量上限击溃 = this.血量上限击溃;
 子弹属性.防御粉碎 = this.防御粉碎;
 子弹属性.lockShooterAttributes = true;

 _root.子弹区域shoot传递(子弹属性);
 stop();
 this.removeMovieClip();

onClipEvent(load){
	子弹属性 = _root.子弹属性初始化(this);

	//以下部分只需要更改需要更改的属性,其余部分可注释掉
	子弹属性.声音 = "";
	子弹属性.霰弹值 = 1;
	子弹属性.子弹散射度 = 0;
	子弹属性.子弹种类 = "近战子弹";
	子弹属性.子弹威力 =  _parent._parent.空手攻击力;
	子弹属性.子弹速度 = 0;
	子弹属性.Z轴攻击范围 = 30;
	子弹属性.击倒率 = 10;
	子弹属性.击中后子弹的效果 = "高爆爆炸";

	//非常用
	/*
	子弹属性.水平击退速度 = NaN;
	子弹属性.垂直击退速度 = NaN;
	子弹属性.发射效果 = "";
	子弹属性.击中地图效果 = "高爆爆炸".
	子弹属性.击中后子弹的效果 = "高爆爆炸".
	子弹属性.命中率 = NaN.
	子弹属性.固伤 = NaN;
	子弹属性.百分比伤害 = NaN;
	子弹属性.血量上限击溃 = NaN;
	子弹属性.防御粉碎 = NaN;
	*/
	子弹属性.区域定位area = this.area;
	/*
	//已初始化内容，通常不需要重复赋值，可注释掉
	子弹属性.发射者 = 子弹属性.发射者.
	子弹属性.shootX = 子弹属性.shootX;
	子弹属性.shootY = 子弹属性.shootY;
	子弹属性.shootZ = 子弹属性.shootZ;
	子弹属性.子弹敌我属性 = 子弹属性.子弹敌我属性;
	*/
	_root.子弹区域shoot传递(子弹属性);
}

onClipEvent(load){
	子弹属性 = _root.子弹属性初始化(this);

	//以下部分只需要更改需要更改的属性,其余部分可注释掉
	子弹属性.声音 = "";
	子弹属性.霰弹值 = 1;
	子弹属性.子弹散射度 = 0;
	子弹属性.子弹种类 = "近战子弹";
