	子弹属性.子弹速度 = 0;
	子弹属性.Z轴攻击范围 = 30;
	子弹属性.击倒率 = 10;
	子弹属性.击中后子弹的效果 = "高爆爆炸";

	//非常用
	/*
	子弹属性.水平击退速度 = NaN;
	子弹属性.垂直击退速度 = NaN;
	子弹属性.发射效果 = "";
