Actionscript.script:
自机 = _root.获得父节点(this, 5);
自机.火箭重拳许可 = true;
this.onUnload = function()
{
	自机.火箭重拳许可 = false;
};
if (自机._name == _root.控制目标)
{
	//if(_root.装备切换状态.火箭重拳){
	if(_root.物品栏.装备栏.getItem("手部装备").value.火箭重拳){
		自机.火箭重拳 = true;
	}
}

onClipEvent (load) {
	var 自机 = _parent.自机 ? _parent.自机 : _root.获得父节点(this, 6);
	if (自机.火箭重拳)
	{
		_parent.play();
	}
	else
	{
		_parent.stop();
	}
	按键中 = false;
}
onClipEvent (enterFrame) {
	if (自机.火箭重拳蓄力中)
	{
		_parent.gotoAndPlay("充能状态");
	}
	else if (自机._name == _root.控制目标 && 自机.攻击模式 === "空手")
	{
		if (Key.isDown(_root.武器变形键))
		{
			if (!按键中)
			{
				if (自机.火箭重拳)
				{
					_root.发布消息("火箭重拳关闭！");
					自机.火箭重拳 = false;
					if (自机._name == _root.控制目标){
						_root.物品栏.装备栏.getItem("手部装备").value.火箭重拳 = false;
					}
					_parent.gotoAndStop(2);
				}
				else
				{
					_root.播放音效("触电.wav");
					_root.发布消息("火箭重拳开启！");
					自机.火箭重拳 = true;
					if (自机._name == _root.控制目标){
						_root.物品栏.装备栏.getItem("手部装备").value.火箭重拳 = true;
					}
					_parent.gotoAndPlay(2);
				}
			}
			按键中 = true;
		}
		else
		{
			按键中 = false;
		}
	}
}

onClipEvent (load) {
	var 自机 = _parent.自机?  _parent.自机 : _root.获得父节点(this, 6);
}
onClipEvent (enterFrame) {
	if(!自机.火箭重拳蓄力中){
		//_root.播放音效("火箭重拳最后一拳.wav");
		_parent.gotoAndPlay(2);
	}
}
