function 攻击模式切换(模式)
{
   if(模式 == "空手")
   {
      攻击模式 = 模式;
      gotoAndStop("攻击模式切换");
   }
   if(模式 == "手雷" and 手雷 != "")
   {
      攻击模式 = 模式;
      gotoAndStop("攻击模式切换");
   }
   if(模式 == "长枪" and 长枪 != "")
   {
      攻击模式 = 模式;
      gotoAndStop("攻击模式切换");
   }
   if(模式 == "兵器" and 刀 != "")
   {
      攻击模式 = 模式;
      gotoAndStop("攻击模式切换");
   }
   if(模式 == "手枪")
   {
      if(手枪2 != "" and 手枪 != "")
      {
         攻击模式 = "双枪";
         gotoAndStop("攻击模式切换");
      }
      else if(手枪 != "")
      {
         攻击模式 = "手枪";
         gotoAndStop("攻击模式切换");
      }
      else if(手枪2 != "")
      {
         攻击模式 = "手枪2";
         gotoAndStop("攻击模式切换");
      }
   }
   if(_root.控制目标 == this._name)
   {
      _root.玩家必要信息界面.gotoAndStop(攻击模式);
   }
}
function 按键控制攻击模式()
{
   if(Key.isDown(_root.键1))
   {
      攻击模式切换("空手");
   }
   else if(Key.isDown(_root.键2))
   {
      攻击模式切换("兵器");
   }
   else if(Key.isDown(_root.键3))
   {
      攻击模式切换("手枪");
   }
   else if(Key.isDown(_root.键4))
   {
      攻击模式切换("长枪");
   }
   else if(Key.isDown(_root.键5))
   {
      攻击模式切换("手雷");
   }
}
function 随机切换攻击模式()
{
   攻击模式随机数 = random(5);
   if(攻击模式随机数 == 0)
   {
      攻击模式切换("空手");
   }
   else if(攻击模式随机数 == 1 and 刀 != "")
   {
      攻击模式切换("兵器");
   }
   else if(攻击模式随机数 == 2)
   {
      if(手枪 != "" or 手枪2 != "")
      {
         攻击模式切换("手枪");
      }
   }
   else if(攻击模式随机数 == 3 and 长枪 != "")
   {
      攻击模式切换("长枪");
   }
   else if(攻击模式随机数 == 4 and 手雷 != "")
   {
      攻击模式切换("手雷");
   }
}
