	}
}

onClipEvent(load){
	if(_parent.等级>=15){
		_parent.刚体 = true;
	}
	_parent.背枪 = true;
}
onClipEvent(unload){
   _parent.刚体 = false;
   _parent.背枪 = false;
   _parent.无敌 = false;
   _parent.浮空 = false;
   _parent.不反击 = false;

}

onClipEvent(load){
	if(_parent.等级>=65){
		_parent.刚体 = true;
	}
   _parent.背枪 = true;
}
onClipEvent(unload){
   _parent.刚体 = false;
   _parent.背枪 = false;
}

onClipEvent(load){
   gotoAndPlay(random(15));
   躲闪 = true;
}
onClipEvent(unload){
   躲闪 = false;
}

onClipEvent(load){
   _parent.浮空 = true;
   gotoAndPlay(_parent.攻击模式);
}
onClipEvent(unload){
   _parent.浮空 = false;
}
onClipEvent(load){
   落地 = false;
   垂直速度 = _parent.起跳速度;
   起始Y = _parent._y;
   this.onEnterFrame = function()
   {
      if(_parent.硬直中 == false)
      {
         _parent._y += 垂直速度;
         垂直速度 += _root.重力加速度;
         if(_parent._y >= _parent.Z轴坐标)
         {
            _parent._y = 起始Y;
            落地 = true;
            delete this.onEnterFrame;
            _parent.状态改变("倒地");
         }
      }
   };
}

onClipEvent(load){
   _parent._y = _parent.Z轴坐标;
   _parent.倒地 = true;
   _parent.格斗架势 = true;
}
onClipEvent(unload){
   _parent.倒地 = false;
}
