onClipEvent(enterFrame){
   自机 = _root.获得父节点(this,5);
   if(自机.状态 == "兵器攻击")
   {
      if(random(30) == 0)
      {
         var myPoint = {x:this._x,y:this._y};
         _parent.localToGlobal(myPoint);
         _root.gameworld.globalToLocal(myPoint);

         自机.空手攻击力 *= 1.05;
         _root.发布消息("神锋嗜血……");

         自机 = true;
         声音 = "";
         霰弹值 = 1;
         子弹散射度 = 0;
         发射效果 = "";
         子弹种类 = "血痕爪杀";
         子弹威力 = 自机.空手攻击力 + 自机.hp;
         子弹速度 = 0;
         击中地图效果 = "";
         Z轴攻击范围 = 200;
         击倒率 = 50;
         击中后子弹的效果 = "";
         子弹敌我属性 = true;
         发射者名 = 自机._name;
         if(自机.是否为敌人 == true)
         {
            子弹敌我属性值 = false;
         }
         else
         {
            子弹敌我属性值 = true;
         }
         shootX = myPoint.x;
         Z轴坐标 = shootY = 自机._y;
         _root.子弹区域shoot(声音,霰弹值,子弹散射度,发射效果,子弹种类,子弹威力,子弹速度,Z轴攻击范围,击中地图效果,发射者名,shootX,shootY,Z轴坐标,子弹敌我属性值,击倒率,击中后子弹的效果);
      }
   }
}

