Actionscript.script:
function 射击()
{
   if(_parent.是否允许发送联机数据 == true)
   {
      clearInterval(_parent.keepshooting);
   }
   if(_parent.长枪射击次数 >= 弹夹容量)
   {
      delete this.onEnterFrame;
      换弹夹();
   }
   else
   {
      _parent.长枪射击次数++;
      if(_root.控制目标 == _parent._name)
      {
         _root.玩家必要信息界面.子弹数 = 弹夹容量 - _parent.长枪射击次数;
      }
      _root.子弹区域shoot(声音,霰弹值,子弹散射度,发射效果,子弹种类,子弹威力,子弹速度,Z轴攻击范围,击中地图效果,_parent._name,shootX,shootY,_parent.Z轴坐标,子弹敌我属性,击倒率,击中后子弹的效果);

      if(长枪是否单发 == "true")
      {
         _parent.单发枪计时();
      }
      if(_root.控制目标全自动 == false and _parent.操控编号 != -1)
      {
         if(Key.isDown(_parent.下键))
         {
            if(射击方向传值 != 1)
            {
               射击方向传值 = 1;
            }
            gotoAndStop("下射击");
            play();
         }
         else if(Key.isDown(_parent.上键))
         {
            if(射击方向传值 != 2)
            {
               射击方向传值 = 2;
            }
            gotoAndStop("上射击");
            play();
         }
         else
         {
            if(射击方向传值 != 0)
            {
               射击方向传值 = 0;
            }
            gotoAndStop("射击");
            play();
         }
      }
   }
}
function 换弹夹()
{
   delete this.onEnterFrame;
   clearInterval(_parent.keepshooting);
   if(_root.控制目标 == _parent._name)
   {
      i2 = 0;
      while(i2 < _root.物品栏总数)
      {
         if(_root.物品栏[i2][0] == 使用弹夹名称)
         {
            if(_root.物品栏[i2][1] >= 1)
            {
               gotoAndStop("换弹夹");
               play();
               break;
            }
            _root.发布消息("弹夹耗尽！");
            _root.物品栏[i2] = ["空",0,0];
            _root.排列物品图标();
            if(_parent.长枪射击次数 >= 弹夹容量)
            {
               gotoAndStop("结束");
            }
            break;
         }
         i2++;
      }
   }
   else if(_parent.操控编号 != -1 && _root.playerCurrent != _parent.操控编号)
   {
      _root.发布消息("其他人物子弹检查。。。");
      _root.发布消息(_root.playerData[_parent.操控编号][2].length);
      var _loc4_ = 0;
      while(_loc4_ < _root.playerData[_parent.操控编号][2].length)
      {
         if(_root.playerData[_parent.操控编号][2][_loc4_][0] == 使用弹夹名称)
         {
            if(_root.playerData[_parent.操控编号][2][_loc4_][1] >= 1)
            {
               _root.playerData[_parent.操控编号][2][_loc4_][1]--;
               _root.排列物品图标();
               _parent.长枪射击次数 = 0;
               gotoAndStop("换弹夹");
               play();
               break;
            }
            _root.发布消息("弹夹耗尽！");
            _root.playerData[_parent.操控编号][2][_loc4_] = ["空",0,0];
            if(_parent.长枪射击次数 >= 弹夹容量)
            {
               gotoAndStop("结束");
            }
            break;
         }
         _loc4_ += 1;
      }
   }
   else
   {
      _parent.长枪射击次数 = 0;
      gotoAndStop("换弹夹");
      play();
   }
}
function 检查下是否有弹夹()
{
   var _loc3_ = 0;
   if(_loc3_ < _root.物品栏总数)
   {
      if(_root.物品栏[_loc3_][0] == 使用弹夹名称)
      {
         return _root.物品栏[_loc3_][1];
      }
      return 0;
   }
   if(_parent.操控编号 != -1 && _root.playerCurrent != _parent.操控编号)
   {
      _root.发布消息("检查弹夹2: " + _parent._name);
      _loc3_ = 0;
      while(_loc3_ < _root.playerData[_parent.操控编号][2].length)
      {
         if(_root.playerData[_parent.操控编号][2][_loc3_][0] == 使用弹夹名称)
         {
            return _root.playerData[_parent.操控编号][2][_loc3_][1];
         }
         _loc3_ += 1;
      }
      _root.发布消息("其他玩家弹夹耗尽！");
   }
   return 0;
}

射击速度 = _parent.长枪属性.interval;
长枪是否单发 = _parent.长枪属性.singleshoot;
弹夹容量 = _parent.长枪属性.capacity;
使用弹夹名称 = _parent.长枪属性.clipname;
声音 = _parent.长枪属性.sound;
霰弹值 = _parent.长枪属性.split;
子弹散射度 = _parent.长枪属性.diffusion;
发射效果 = _parent.长枪属性.muzzle;
子弹种类 = _parent.长枪属性.bullet;
子弹威力 = _parent.长枪属性.power * 1.5 + 30 + _parent.长枪属性.power * 2 * (_root.等级 / 100);
子弹速度 = _parent.长枪属性.velocity;
击中地图效果 = _parent.长枪属性.bullethit;
Z轴攻击范围 = _parent.长枪属性.bulletsize;
击倒率 = _parent.长枪属性.impact;
击中后子弹的效果 = _parent.长枪属性.targethit;
if(_parent.是否为敌人 == true)
{
   子弹敌我属性 = false;
}
else
{
   子弹敌我属性 = true;
}
var myPoint = {x:this.枪.枪.装扮.枪口位置._x,y:this.枪.枪.装扮.枪口位置._y};
this.枪.枪.装扮.localToGlobal(myPoint);
if(myPoint.y == this.枪.枪.装扮.枪口位置._y)
{
   _parent.长枪射击ing = false;
   _parent.动画完毕();
}
_root.gameworld.globalToLocal(myPoint);
shootX = myPoint.x;
shootY = myPoint.y;
剩余弹夹数 = 检查下是否有弹夹();
if(_parent.强制换弹夹 == true and 剩余弹夹数 >= 0 and _parent.长枪射击次数 > 0)
{
   换弹夹();
}
else
{
   _parent.强制换弹夹 = false;
}
if(剩余弹夹数 <= 0 and _parent.长枪射击次数 >= 弹夹容量 and _parent.强制换弹夹 != true)
{
   delete this.onEnterFrame;
   clearInterval(_parent.keepshooting);
   _parent.UpdateSmallState("长枪攻击结束");
   gotoAndStop("空枪");
   play();
}
else if(剩余弹夹数 >= 0 and _parent.长枪射击次数 < 弹夹容量 and _parent.强制换弹夹 != true)
{
   if(长枪是否单发 == "true")
   {
      if(_parent.单发枪计时_时间结束 == true)
      {
         _parent.UpdateSmallState("长枪攻击中");
         射击();
      }
      else
      {
         _parent.UpdateSmallState("长枪攻击结束");
         gotoAndStop("结束");
      }
   }
   else
   {
      _parent.UpdateSmallState("长枪攻击中");
      射击();
   }
   _parent.keepshooting = setInterval(射击,射击速度);
}
结束攻击计数 = 0;
结束攻击数 = random(200);
this.onEnterFrame = function()
{
   if(_parent.长枪射击次数 >= 弹夹容量)
   {
      换弹夹();
      _parent.UpdateSmallState("长枪攻击结束");
      delete this.onEnterFrame;
   }
   if(_root.控制目标全自动 == false and _parent.操控编号 != -1 and Key.isDown(_parent.A键) == false)
   {
      clearInterval(_parent.keepshooting);
      _parent.长枪射击ing = false;
      _parent.UpdateSmallState("长枪攻击结束");
      _parent.动画完毕();
   }
   结束攻击计数++;
   if(结束攻击数 < 结束攻击计数)
   {
      _parent.长枪射击ing = false;
      _parent.UpdateSmallState("长枪攻击结束");
      _parent.动画完毕();
   }
};
this.onUnload = function()
{
   _parent.长枪射击ing = false;
   clearInterval(_parent.keepshooting);
   delete this.onEnterFrame;
};
stop();

