Actionscript.script:
性别 = "女";
this.初始化玩家模板 = _root.初始化玩家模板;
this.初始化玩家模板();

onClipEvent(load){
   this._visible = 0;
   if(_parent.操控目标 == -1)
   {
      gotoAndStop(2);
   }
}

onClipEvent(load){
   this._visible = 0;
   if(_parent.操控目标 == -1)
   {
      gotoAndStop(2);
   }
}

onClipEvent(load){
   this._visible = 0;
   if(_parent.操控目标 == -1)
   {
