on(press){
   if(是否为敌人 == true and _root.全鼠标控制 == true and this.hp > 0)
   {
      _root.gameworld[_root.控制目标].攻击目标 = this._name;
      _root.控制目标全自动 = true;
   }
}
on(press, release, rollOver){
   if(是否为敌人 == true and _root.全鼠标控制 == true and this.hp > 0)
   {
      _root.鼠标.gotoAndStop("手型攻击");
   }
}
on(rollOut){
   _root.鼠标.gotoAndStop("手型普通");
}
on(release){
   if(是否为敌人 != true)
   {
      if(this._name == _root.控制目标)
      {
         _root.查看用户ID = 0;
         _root.菜单MC对应名 = this._name;
         _root.修改详细菜单._visible = 1;
         _root.修改详细菜单._x = _root._xmouse;
         _root.修改详细菜单._y = _root._ymouse;
      }
      else
      {
         _root.查看用户ID = this.用户ID;
         _root.菜单MC对应名 = this._name;
         _root.查看详细菜单._visible = 1;
         _root.查看详细菜单._x = _root._xmouse;
         _root.查看详细菜单._y = _root._ymouse;
      }
   }
}

onClipEvent(enterFrame){
   旧上行 = _parent.上行;
   旧下行 = _parent.下行;
   旧左行 = _parent.左行;
   旧右行 = _parent.右行;
   _parent.行走();
   if(_root.暂停 == false)
   {
      if(_parent.操控编号 != -1)
      {
         if(_root.控制目标全自动 != true and _root.全鼠标控制 == false)
         {
            _parent.按键控制攻击模式();
            if(Key.isDown(_parent.A键))
            {
               _parent.格斗架势 = true;
               _parent.状态改变(_parent.攻击模式 + "攻击");
            }
            if(Key.isDown(_parent.B键))
            {
               var now = new Date();
               if(now.getTime() - _parent.last > 1000)
               {
                  _parent.格斗架势 = true;
                  _parent.长枪副攻击 = true;
                  _parent.状态改变(_parent.攻击模式 + "攻击");
               }
            }
            if(Key.isDown(_parent.C键))
            {
               _parent.强制换弹夹 = true;
               _parent.格斗架势 = true;
               _parent.状态改变(_parent.攻击模式 + "攻击");
            }
            if(Key.isDown(_parent.左键))
            {
               _parent.左行 = 1;
            }
            else
            {
               _parent.左行 = 0;
            }
            if(Key.isDown(_parent.右键))
            {
               _parent.右行 = 1;
            }
            else
            {
               _parent.右行 = 0;
            }
            if(Key.isDown(_parent.上键))
            {
               _parent.上行 = 1;
            }
            else
            {
               _parent.上行 = 0;
            }
            if(Key.isDown(_parent.下键))
            {
               _parent.下行 = 1;
            }
            else
            {
               _parent.下行 = 0;
            }
         }
      }
      if(_root.控制目标 == _parent._name and _root.全鼠标控制 == true and _root.控制目标全自动 != true)
      {
         if(_root.gameworld鼠标横向位置 < _parent._x)
         {
            _root.gameworld[_root.控制目标].左行 = 1;
            _root.gameworld[_root.控制目标].右行 = 0;
         }
         else if(_root.gameworld鼠标横向位置 > _parent._x)
         {
            _root.gameworld[_root.控制目标].右行 = 1;
            _root.gameworld[_root.控制目标].左行 = 0;
         }
         if(_root.gameworld鼠标纵向位置 < _parent._y)
         {
            _root.gameworld[_root.控制目标].上行 = 1;
            _root.gameworld[_root.控制目标].下行 = 0;
         }
         else if(_root.gameworld鼠标纵向位置 > _parent._y)
         {
            _root.gameworld[_root.控制目标].下行 = 1;
            _root.gameworld[_root.控制目标].上行 = 0;
         }
         if(Math.abs(_root.gameworld鼠标横向位置 - _parent._x) < 20)
         {
            _root.gameworld[_root.控制目标].右行 = 0;
            _root.gameworld[_root.控制目标].左行 = 0;
         }
         if(Math.abs(_root.gameworld鼠标纵向位置 - _parent._y) < 20)
         {
            _root.gameworld[_root.控制目标].下行 = 0;
            _root.gameworld[_root.控制目标].上行 = 0;
         }
      }
   }
   else if(_root.暂停 == true)
   {
      _root.gameworld[_root.控制目标].右行 = 0;
      _root.gameworld[_root.控制目标].左行 = 0;
      _root.gameworld[_root.控制目标].下行 = 0;
      _root.gameworld[_root.控制目标].上行 = 0;
   }
   if(旧上行 != _parent.上行 || 旧下行 != _parent.下行 || 旧左行 != _parent.左行 || 旧右行 != _parent.右行)
   {
      _parent.联机2015发送角色数据();
   }
}

onClipEvent(enterFrame){
   this._rotation = _parent.man._rotation;
}

