      _root.gameworld[_root.控制目标].左行 = 0;
      _root.gameworld[_root.控制目标].下行 = 0;
      _root.gameworld[_root.控制目标].上行 = 0;
   }
}

onClipEvent(enterFrame){
   _parent.行走();
   if(_root.暂停 == false)
   {
      if(_parent.操控编号 != -1)
      {
         if(_root.控制目标全自动 != true and _root.全鼠标控制 == false)
         {
            _parent.按键控制攻击模式();
            if(Key.isDown(_parent.A键))
            {
               _parent.格斗架势 = true;
               if(_parent.状态 == _parent.攻击模式 + "跑")
               {
                  _parent.状态改变(_parent.攻击模式 + "冲击");
               }
               else
               {
                  _parent.状态改变(_parent.攻击模式 + "攻击");
               }
            }
            if(Key.isDown(_parent.B键))
            {
               _parent.跳();
            }
            if(Key.isDown(_parent.左键))
            {
               _parent.左行 = 1;
            }
            else
            {
               _parent.左行 = 0;
            }
            if(Key.isDown(_parent.右键))
            {
               _parent.右行 = 1;
            }
            else
            {
               _parent.右行 = 0;
            }
            if(Key.isDown(_parent.上键))
            {
               _parent.上行 = 1;
            }
            else
            {
               _parent.上行 = 0;
            }
            if(Key.isDown(_parent.下键))
            {
               _parent.下行 = 1;
            }
            else
            {
               _parent.下行 = 0;
            }
         }
      }
      if(_root.控制目标 == _parent._name and _root.全鼠标控制 == true and _root.控制目标全自动 != true)
      {
         if(_root.gameworld鼠标横向位置 < _parent._x)
         {
            _root.gameworld[_root.控制目标].左行 = 1;
            _root.gameworld[_root.控制目标].右行 = 0;
         }
         else if(_root.gameworld鼠标横向位置 > _parent._x)
         {
            _root.gameworld[_root.控制目标].右行 = 1;
            _root.gameworld[_root.控制目标].左行 = 0;
         }
         if(_root.gameworld鼠标纵向位置 < _parent._y)
         {
            _root.gameworld[_root.控制目标].上行 = 1;
            _root.gameworld[_root.控制目标].下行 = 0;
         }
         else if(_root.gameworld鼠标纵向位置 > _parent._y)
         {
            _root.gameworld[_root.控制目标].下行 = 1;
            _root.gameworld[_root.控制目标].上行 = 0;
         }
         if(Math.abs(_root.gameworld鼠标横向位置 - _parent._x) < 20)
         {
            _root.gameworld[_root.控制目标].右行 = 0;
            _root.gameworld[_root.控制目标].左行 = 0;
         }
         if(Math.abs(_root.gameworld鼠标纵向位置 - _parent._y) < 20)
         {
            _root.gameworld[_root.控制目标].下行 = 0;
            _root.gameworld[_root.控制目标].上行 = 0;
         }
      }
   }
   else if(_root.暂停 == true)
   {
      _root.gameworld[_root.控制目标].右行 = 0;
      _root.gameworld[_root.控制目标].左行 = 0;
      _root.gameworld[_root.控制目标].下行 = 0;
      _root.gameworld[_root.控制目标].上行 = 0;
   }
}

onClipEvent(load){
   if(_parent._name == _root.gameworld[_root.控制目标]._name)
   {
      _parent.读取当前飞行状态();
   }
   if(_parent.格斗架势 == false)
   {
      gotoAndStop("站立");
      play();
   }
   else if(_parent.格斗架势 == true)
   {
      gotoAndStop("格斗架势");
      play();
   }
}

onClipEvent(enterFrame){
   if(Key.isDown(_parent.A键) and this._currentframe == 1)
   {
      this.gotoAndPlay("空中攻击");
      飞踢许可 = true;
   }
   if(this._currentframe == 8)
   {
      飞踢许可 = false;
   }
   if(this._currentframe > 1 and !飞踢许可)
   {
      this.gotoAndStop(1);
   }
   _parent._y += _parent.垂直速度;
   _parent.垂直速度 += _root.重力加速度;
   if(_parent._y >= _parent.起始Y)
   {
      _parent._y = _parent.起始Y;
      落地 = true;
      _root.效果("灰尘1",_parent._x,_parent._y,_parent._xscale);
	  _root.播放音效("soundland.wav");
      _parent.动画完毕();
   }
   if(_parent.跳跃中上下方向 == "上")
   {
      _parent.跳跃上下移动("上",_parent.跳横移速度 / 2);
   }
   else if(_parent.跳跃中上下方向 == "下")
   {
      _parent.跳跃上下移动("下",_parent.跳横移速度 / 2);
   }
   if(_parent.跳跃中左右方向 == "右")
   {
      _parent.移动("右",_parent.跳横移速度);
   }
   else if(_parent.跳跃中左右方向 == "左")
   {
      _parent.移动("左",_parent.跳横移速度);
   }
   if(Key.isDown(_parent.右键) == true)
   {
      _parent.跳跃中左右方向 = "右";
      _parent.方向改变("右");
      _parent.左行 = 0;
      _parent.右行 = 1;
      if(_parent.跳横移速度 == 0)
      {
         _parent.移动("右",_parent.跳跃中移动速度);
      }
   }
   else if(Key.isDown(_parent.左键) == true)
   {
      _parent.跳跃中左右方向 = "左";
      _parent.方向改变("左");
      _parent.右行 = 0;
      _parent.左行 = 1;
      if(_parent.跳横移速度 == 0)
      {
         _parent.移动("左",_parent.跳跃中移动速度);
      }
   }
   else
   {
      _parent.跳跃中左右方向 = "无";
   }
   if(Key.isDown(_parent.上键) == true)
   {
      _parent.跳跃中上下方向 = "上";
      if(_parent.跳横移速度 == 0)
      {
         _parent.跳跃上下移动("上",_parent.跳跃中移动速度 / 2);
      }
   }
   else if(Key.isDown(_parent.下键) == true)
   {
      _parent.跳跃中上下方向 = "下";
      if(_parent.跳横移速度 == 0)
      {
         _parent.跳跃上下移动("下",_parent.跳跃中移动速度 / 2);
      }
   }
   else
   {
      _parent.跳跃中上下方向 = "无";
   }
}
