onClipEvent(load){
   function 检查下是否有弹夹()
   {
      i = 0;
      while(i < _root.物品栏总数)
      {
         if(_root.物品栏[i][0] == 使用弹夹名称)
         {
            return _root.物品栏[i][1];
         }
         i++;
      }
      return 0;
   }
   if(_parent._name == _root.gameworld[_root.控制目标]._name)
   {
      _parent.读取当前飞行状态();
   }
   if(_parent.格斗架势 == false)
   {
      gotoAndStop("站立");
      play();
   }
   else if(_parent.格斗架势 == true)
   {
      gotoAndStop("格斗架势");
      play();
   }
   if(_root.控制目标 == _parent._name)
   {
      弹夹容量 = _parent.长枪属性.capacity;
      使用弹夹名称 = _parent.长枪属性.clipname;
      剩余弹夹数 = 检查下是否有弹夹();
      _root.玩家必要信息界面.子弹数 = 弹夹容量 - _parent.长枪射击次数;
      _root.玩家必要信息界面.弹夹数 = 剩余弹夹数;
   }
}

