onClipEvent(enterFrame){
   function 榴弹射击()
   {
       /*_声音 = shoot.wav.长枪属性.sound;
      _霰弹值 = 1;
      长枪属性.split;
      _子弹散射度 = 1;
      长枪属性.diffusion;
      _发射效果 = 枪火.长枪属性.muzzle;
      _子弹种类 = "普通手雷".长枪属性.bullet;
      _子弹威力 = 5000;
      长枪属性.power;
      _子弹速度 = 20;
      长枪属性.velocity;
      _击中地图效果 = 火花.长枪属性.bullethit;
      _Z轴攻击范围 = 30;
      长枪属性.bulletsize;
      _击倒率 = 0;
      长枪属性.impact;
      _击中后子弹的效果 = 火花.长枪属性.targethit;*/
      if(_parent.是否为敌人 == true)
      {
         _子弹敌我属性 = false;
      }
      else
      {
         _子弹敌我属性 = true;
      }
      var _loc4_ = {x:this.枪.枪.装扮.枪口位置._x,y:this.枪.枪.装扮.枪口位置._y};
      this.枪.枪.装扮.localToGlobal(_loc4_);
      if(_loc4_.y == this.枪.枪.装扮.枪口位置._y)
      {
         _parent.长枪射击ing = false;
         _parent.动画完毕();
      }
      _root.gameworld.globalToLocal(_loc4_);
      _shootX = _loc4_.x;
      _shootY = _loc4_.y;
      var _loc5_ = 0;
      var _loc6_ = false;
      while(_loc5_ < _root.物品栏总数)
      {
         if(_root.物品栏[_loc5_][0] == "普通手雷" and _root.物品栏[_loc5_][1] >= 1)
         {
            _root.物品栏[_loc5_][1]--;
            _root.排列物品图标();
            _loc6_ = true;
         }
         _loc5_ += 1;
      }
      if(_loc6_)
      {
         _root.子弹区域shoot(_声音,_霰弹值,_子弹散射度,_发射效果,"普通手雷",_子弹威力,_子弹速度,_Z轴攻击范围,_击中地图效果,_parent._name,_shootX,_shootY,_parent.Z轴坐标,_子弹敌我属性,_击倒率,_击中后子弹的效果);
      }
   }
   if(_parent.长枪副攻击 and _root.长枪 == "M4A1改")
   {
      榴弹射击();
      _parent.长枪副攻击 = false;
      var now = new Date();
      _parent.last = now.getTime();
   }
   else if(Key.isDown(_parent.B键))
   {
      var now = new Date();
      if(now.getTime() - _parent.last > 1000)
      {
         _parent.长枪副攻击 = true;
      }
   }
   if(Key.isDown(_parent.左键))
   {
      _parent.方向改变("左");
   }
   if(Key.isDown(_parent.右键))
   {
      _parent.方向改变("右");
   }
}
