         _root.子弹区域shoot(_声音,_霰弹值,_子弹散射度,_发射效果,"普通手雷",_子弹威力,_子弹速度,_Z轴攻击范围,_击中地图效果,_parent._name,_shootX,_shootY,_parent.Z轴坐标,_子弹敌我属性,_击倒率,_击中后子弹的效果);
      }
   }
   if(_parent.长枪副攻击 and _root.长枪 == "AK74战术版")
   {
      榴弹射击();
      _parent.长枪副攻击 = false;
      var now = new Date();
      _parent.last = now.getTime();
   }
   else if(Key.isDown(_parent.B键))
   {
      var now = new Date();
      if(now.getTime() - _parent.last > 1000)
      {
         _parent.长枪副攻击 = true;
      }
   }
   if(Key.isDown(_parent.左键))
   {
      _parent.方向改变("左");
   }
   if(Key.isDown(_parent.右键))
   {
      _parent.方向改变("右");
   }
}

onClipEvent(load){
   function 检查下是否有弹夹()
   {
      i = 0;
      while(i < _root.物品栏总数)
      {
         if(_root.物品栏[i][0] == 使用弹夹名称)
         {
            return _root.物品栏[i][1];
         }
         i++;
      }
      return 0;
   }
   if(_parent._name == _root.gameworld[_root.控制目标]._name)
   {
      _parent.读取当前飞行状态();
   }
   if(_parent.格斗架势 == false)
   {
      gotoAndStop("站立");
      play();
   }
   else if(_parent.格斗架势 == true)
   {
      gotoAndStop("格斗架势");
      play();
   }
   if(_root.控制目标 == _parent._name)
   {
      弹夹容量 = _parent.手枪属性.capacity;
      使用弹夹名称 = _parent.手枪属性.clipname;
      剩余弹夹数 = 检查下是否有弹夹();
      _root.玩家必要信息界面.子弹数 = 弹夹容量 - _parent.手枪射击次数;
      _root.玩家必要信息界面.弹夹数 = 剩余弹夹数;
   }
}

onClipEvent(enterFrame){
   if(_parent._xscale > 0)
   {
      _parent.移动("右",_parent.跳横移速度);
   }
   else
   {
      _parent.移动("左",_parent.跳横移速度);
   }
   _parent._y += _parent.垂直速度;
   _parent.垂直速度 += _root.重力加速度;
   if(_parent._y >= _parent.起始Y)
   {
      _parent._y = _parent.起始Y;
      落地 = true;
      _parent.动画完毕();
   }
}
onClipEvent(load){
   落地 = false;
   if(_parent.技能浮空 == true)
   {
      _parent.技能浮空 = false;
      _parent.起始Y = _parent.Z轴坐标;
   }
   else if(_root.temp_player_y > 0 and _parent._name == _root.gameworld[_root.控制目标]._name)
   {
      _parent.起始Y = _parent.Z轴坐标;
   }
   else
   {
      _parent.垂直速度 = _parent.起跳速度;
      _parent.起始Y = _parent._y;
   }
   stop();
}

