onClipEvent(enterFrame){
   if(Key.isDown(_parent.A键) and this._currentframe == 1)
   {
      this.gotoAndPlay("空中攻击");
      跳砍许可 = true;
      _parent.联机2015发送角色数据();
   }
   if(this._currentframe == 10)
   {
      跳砍许可 = false;
   }
   if(this._currentframe > 1 and !跳砍许可)
   {
      this.gotoAndStop(1);
   }
   _parent._y += _parent.垂直速度;
   _parent.垂直速度 += _root.重力加速度;
   if(_parent._y >= _parent.起始Y)
   {
      _parent._y = _parent.起始Y;
      _parent.浮空 = false;
      落地 = true;
      _parent.动画完毕();
   }
   if(_parent.跳跃中上下方向 == "上")
   {
      _parent.跳跃上下移动("上",_parent.跳横移速度 / 2);
   }
   else if(_parent.跳跃中上下方向 == "下")
   {
      _parent.跳跃上下移动("下",_parent.跳横移速度 / 2);
   }
   if(_parent.跳跃中左右方向 == "右")
   {
      _parent.移动("右",_parent.跳横移速度);
   }
   else if(_parent.跳跃中左右方向 == "左")
   {
      _parent.移动("左",_parent.跳横移速度);
   }
   if(Key.isDown(_parent.右键) == true)
   {
      _parent.跳跃中左右方向 = "右";
      _parent.方向改变("右");
      _parent.左行 = 0;
      _parent.右行 = 1;
      if(_parent.跳横移速度 == 0)
      {
         _parent.移动("右",_parent.跳跃中移动速度);
      }
   }
   else if(Key.isDown(_parent.左键) == true)
   {
      _parent.跳跃中左右方向 = "左";
      _parent.方向改变("左");
      _parent.右行 = 0;
      _parent.左行 = 1;
      if(_parent.跳横移速度 == 0)
      {
         _parent.移动("左",_parent.跳跃中移动速度);
      }
   }
   else
   {
      _parent.跳跃中左右方向 = "无";
   }
   if(Key.isDown(_parent.上键) == true)
   {
      _parent.跳跃中上下方向 = "上";
      if(_parent.跳横移速度 == 0)
      {
         _parent.跳跃上下移动("上",_parent.跳跃中移动速度 / 2);
      }
   }
   else if(Key.isDown(_parent.下键) == true)
   {
      _parent.跳跃中上下方向 = "下";
      if(_parent.跳横移速度 == 0)
      {
         _parent.跳跃上下移动("下",_parent.跳跃中移动速度 / 2);
      }
   }
   else
   {
      _parent.跳跃中上下方向 = "无";
   }
}
onClipEvent(load){
   _parent.浮空 = true;
   落地 = false;
   if(_parent.技能浮空 == true)
   {
      _parent.技能浮空 = false;
      _parent.起始Y = _parent.Z轴坐标;
   }
   else if(_root.temp_player_y > 0 and _parent._name == _root.gameworld[_root.控制目标]._name)
   {
      _parent.起始Y = _parent.Z轴坐标;
   }
   else
   {
      _parent.垂直速度 = _parent.起跳速度;
      _parent.起始Y = _parent._y;
   }
   stop();
}

onClipEvent(load){
   if(_parent._name == _root.gameworld[_root.控制目标]._name)
   {
      _parent.读取当前飞行状态();
   }
}

onClipEvent(load){
   if(_parent._name == _root.gameworld[_root.控制目标]._name)
   {
      _parent.读取当前飞行状态();
   }
   if(_parent.产生源 != undefined and _parent.是否为敌人 == true)
   {
	   if(_parent.hp <= 0)
		{
      		_root.gameworld[_parent.产生源].僵尸型敌人场上实际人数--;
      		_root.gameworld[_parent.产生源].僵尸型敌人总个数--;
		}
   }
   _parent._y = _parent.Z轴坐标;
   _parent.倒地 = true;
   _parent.格斗架势 = true;
   gotoAndPlay("a" + random(7));
}
onClipEvent(unload){
   _parent.倒地 = false;
}
