Actionscript.script:
性别 = 性别 ? 性别 : "男";
this.初始化玩家模板 = _root.初始化玩家模板;
this.初始化玩家模板();

格斗架势 = false;
if (状态 === "技能" || 状态 === "战技")
{
	状态改变(攻击模式 + "站立");
}
else
{
	gotoAndStop(状态);
}

