onClipEvent (enterFrame) {
	if(_parent.移动射击倒退){
		if(this._currentframe == 1){
			gotoAndStop("end");
		}else{
			gotoAndStop(this._currentframe-1);
		}
	}else{
		play();
	}
}

onClipEvent (enterFrame) {
	// 重力更新和落地检测已迁移到 _parent.启动跳跃浮空() 定时器管理
	// 此处只保留移动控制逻辑
	if (!落地)
	{
		if (_parent.跳跃中上下方向 == "上")
		{
			_parent.跳跃上下移动("上",_parent.跳横移速度 * 跳跃移动倍率 / 2);
		}
		else if (_parent.跳跃中上下方向 == "下")
		{
			_parent.跳跃上下移动("下",_parent.跳横移速度 * 跳跃移动倍率 / 2);
		}
		if (_parent.跳跃中左右方向 == "右")
		{
			_parent.移动("右",_parent.跳横移速度 * 跳跃移动倍率);
		}
		else if (_parent.跳跃中左右方向 == "左")
		{
			_parent.移动("左",_parent.跳横移速度 * 跳跃移动倍率);
		}
		if (_parent.右行)
		{
			_parent.跳跃中左右方向 = "右";
			_parent.方向改变("右");
			_parent.左行 = 0;
			_parent.右行 = 1;
			if (_parent.跳横移速度 == 0)
			{
				_parent.移动("右",_parent.跳跃中移动速度 * 跳跃移动倍率);
			}
		}
		else if (_parent.左行)
		{
			_parent.跳跃中左右方向 = "左";
			_parent.方向改变("左");
			_parent.右行 = 0;
			_parent.左行 = 1;
			if (_parent.跳横移速度 == 0)
			{
				_parent.移动("左",_parent.跳跃中移动速度 * 跳跃移动倍率);
			}
		}
		else
		{
			_parent.跳跃中左右方向 = "无";
		}
		if (_parent.上行)
		{
			_parent.跳跃中上下方向 = "上";
			if (_parent.跳横移速度 == 0)
			{
				_parent.跳跃上下移动("上",_parent.跳跃中移动速度 * 跳跃移动倍率 / 2);
			}
		}
		else if (_parent.下行)
		{
			_parent.跳跃中上下方向 = "下";
			if (_parent.跳横移速度 == 0)
			{
				_parent.跳跃上下移动("下",_parent.跳跃中移动速度 * 跳跃移动倍率 / 2);
			}
		}
		else
		{
			_parent.跳跃中上下方向 = "无";
		}
	}
}
onClipEvent (load) {
	if (_parent.状态 != "空手跳")
	{
		return;
	}
	// 调用统一的跳跃浮空控制函数（使用定时器管理，与技能浮空系统兼容）
	_parent.启动跳跃浮空(this);
	// 升龙拳检测
	if (_parent._name == _root.控制目标)
	{
		if (_parent.hp>0 && _parent.被动技能.升龙拳 && _parent.被动技能.升龙拳.启用 && Key.isDown(_parent.A键))
		{
			this.gotoAndPlay("上勾拳");
		}
	}
}

onClipEvent(load){
	if (_parent._name == _root.控制目标)
	{
		// 按下攻击键K后触发
		if (_parent.被动技能.升龙拳 && _parent.被动技能.升龙拳.启用 && Key.isDown(_parent.B键))
		{
			_parent.跳横移速度 = _parent.跑X速度;
			_parent.跳跃中移动速度 = _parent.跑X速度;
			_parent.状态改变("空手跳");
		}
	}
   _parent.格斗架势 = true;
}

onClipEvent (load) {
	gotoAndPlay(_parent.攻击模式);
}

onClipEvent(load){
   _parent.格斗架势 = true;
   gotoAndPlay(_parent.攻击模式 + random(2));
}

onClipEvent (load) {
	_parent.格斗架势 = true;
	gotoAndPlay(_parent.攻击模式 + random(2));
}

onClipEvent (enterFrame) {
	// 重力更新和落地检测已迁移到空中控制器
	// 此处只保留 NPC AI 逻辑
	if (!落地)
	{
		if (_parent.hp > 0 && (_parent.名字 == "Andy" || _parent.名字 == "Blue" || _parent.名字 == "Boy") && _parent._name != _root.控制目标)
		{
			temp_随机小跳值 = random(15);
			if (temp_随机小跳值 == 0)
			{
				_root.技能路由.技能标签跳转_旧(_parent, "小跳");
			}
			else if (temp_随机小跳值 == 1 || temp_随机小跳值 == 2)
			{
				_root.技能路由.技能标签跳转_旧(_parent, "上下跳");
			}
			else if (temp_随机小跳值 == 3 && _parent.名字 != "Boy")
			{
				_root.技能路由.技能标签跳转_旧(_parent, "前跳");
			}
		}
	}
}
onClipEvent (load) {
	// 调用统一的被击飞浮空控制函数（使用空中控制器管理）
	_root.启动被击飞浮空(this, _parent);
	gotoAndPlay(_parent.攻击模式);
}

onClipEvent (load) {
	_parent._y = _parent.Z轴坐标;
	_parent.倒地 = true;
	_parent.格斗架势 = true;
	if (_parent._name == _root.控制目标 && !_root.限制系统.DisableKnockdownProtection)
	{
		_parent.无敌 = true;
		_parent.防止无限飞 = true;
	}
	this.onUnload = function()
	{
		_parent.倒地 = false;
		if (_parent._name == _root.控制目标)
		{
			_parent.无敌 = false;
			_parent.防止无限飞 = false;
		}
	};
}

onClipEvent(load){
   if(_parent._name == _root.控制目标)
   {
      _parent.读取当前飞行状态("切换武器");
   }
   _parent.格斗架势 = true;
   gotoAndPlay(_parent.攻击模式);
}

onClipEvent(load){
   _parent.格斗架势 = true;
   gotoAndStop(_parent.攻击模式 + 1);
}

