onClipEvent(load){
	未切换按键 = true;
	if (!_parent.手雷抛出模式)
	{
		_parent.手雷抛出模式 = "高抛";
	}
}
onClipEvent (enterFrame) {
	if (_parent._name == _root.控制目标)
	{
		if (Key.isDown(_root.武器变形键))
		{
			if (未切换按键)
			{
				if (_parent.手雷抛出模式 == "高抛")
				{
					_parent.手雷抛出模式 = "低抛";
				}
				else if (_parent.手雷抛出模式 == "低抛")
				{
					_parent.手雷抛出模式 = "高抛";
				}
				else
				{
					_parent.手雷抛出模式 = "低抛";
				}
				_root.发布消息("手雷抛出模式切换为"+_parent.手雷抛出模式);
			}
			未切换按键 = false;
		}
		else
		{
			未切换按键 = true;
		}
	}
}

