onClipEvent (load) {
	射击方向传值 = 0;
	联机_是否射击 = true;
	_parent.右行 = 0;
	_parent.左行 = 0;
	//_parent.上行 = 0;
	//_parent.下行 = 0;
	if (!_parent.手雷抛出模式)
	{
		_parent.手雷抛出模式 = "高抛";
	}
	未切换按键 = true;
	if (_parent.是否允许发送联机数据 == true)
	{
		_parent.动画完毕();
	}
}
onClipEvent (enterFrame) {

	if (_parent._name == _root.控制目标)
	{
		if (Key.isDown(_parent.左键))
		{
			_parent.方向改变("左");
		}
		if (Key.isDown(_parent.右键))
		{
			_parent.方向改变("右");
		}
		if (Key.isDown(_root.武器变形键))
		{
			if (未切换按键)
			{
				if (_parent.手雷抛出模式 == "高抛")
				{
					_parent.手雷抛出模式 = "低抛";
				}
				else if (_parent.手雷抛出模式 == "低抛")
				{
					_parent.手雷抛出模式 = "高抛";
				}
				else
				{
					_parent.手雷抛出模式 = "低抛";
				}
				_root.发布消息("手雷抛出模式切换为" + _parent.手雷抛出模式);
				_parent.手雷动作类型 = _parent.手雷动作类型? _parent.手雷动作类型 :"";
				跳转帧 = _parent.手雷动作类型 + _parent.手雷抛出模式;
				this.gotoAndPlay(跳转帧);
			}
			未切换按键 = false;
		}
		else
		{
			未切换按键 = true;
		}
	}
}

