onClipEvent (load) {
	if (_parent.兵器动作类型 == "短兵")
	{
		if (_parent.格斗架势 == false)
		{
			gotoAndPlay("短兵站立");
		}
		else
		{
			gotoAndPlay("短兵格斗架势");
		}

	}
	else if (_parent.兵器动作类型 == "双刀")
	{
		if (_parent.格斗架势 == false)
		{
			gotoAndPlay("双刀站立");
		}
		else
		{
			gotoAndPlay("双刀格斗架势");
		}
	}
	else
	{
		if (_parent.格斗架势 == false)
		{
			gotoAndStop("站立");
			play();
		}
		else if (_parent.格斗架势 == true)
		{
			gotoAndStop("格斗架势");
			play();
		}
	}
}

onClipEvent (enterFrame) {
	// 重力更新和落地检测已迁移到 _parent.启动跳跃浮空() 定时器管理
	// 此处只保留移动控制逻辑
	if (!落地)
	{
		if (_parent.跳跃中上下方向 == "上")
		{
			_parent.跳跃上下移动("上",_parent.跳横移速度 * 跳跃移动倍率 / 2);
		}
		else if (_parent.跳跃中上下方向 == "下")
		{
			_parent.跳跃上下移动("下",_parent.跳横移速度 * 跳跃移动倍率 / 2);
		}
		if (_parent.跳跃中左右方向 == "右")
		{
			_parent.移动("右",_parent.跳横移速度 * 跳跃移动倍率);
		}
		else if (_parent.跳跃中左右方向 == "左")
		{
			_parent.移动("左",_parent.跳横移速度 * 跳跃移动倍率);
		}
		if (Key.isDown(_parent.右键) == true)
		{
			_parent.跳跃中左右方向 = "右";
			_parent.方向改变("右");
			_parent.左行 = 0;
			_parent.右行 = 1;
			if (_parent.跳横移速度 == 0)
			{
				_parent.移动("右",_parent.跳跃中移动速度 * 跳跃移动倍率);
			}
		}
		else if (Key.isDown(_parent.左键) == true)
		{
			_parent.跳跃中左右方向 = "左";
			_parent.方向改变("左");
			_parent.右行 = 0;
			_parent.左行 = 1;
			if (_parent.跳横移速度 == 0)
			{
				_parent.移动("左",_parent.跳跃中移动速度 * 跳跃移动倍率);
			}
		}
		else
		{
			_parent.跳跃中左右方向 = "无";
		}
		if (Key.isDown(_parent.上键) == true)
		{
			_parent.跳跃中上下方向 = "上";
			if (_parent.跳横移速度 == 0)
			{
				_parent.跳跃上下移动("上",_parent.跳跃中移动速度 * 跳跃移动倍率 / 2);
			}
		}
		else if (Key.isDown(_parent.下键) == true)
		{
			_parent.跳跃中上下方向 = "下";
			if (_parent.跳横移速度 == 0)
			{
				_parent.跳跃上下移动("下",_parent.跳跃中移动速度 * 跳跃移动倍率 / 2);
			}
		}
		else
		{
			_parent.跳跃中上下方向 = "无";
		}
	}
}
onClipEvent (load) {
	if (_parent.状态 != "兵器跳")
	{
		return;
	}
	// 调用统一的跳跃浮空控制函数（使用定时器管理，与技能浮空系统兼容）
	_parent.启动跳跃浮空(this);
	// 上挑检测
	if (_parent._name == _root.控制目标)
	{
		if (_parent.hp > 0 && _parent.被动技能.上挑 && _parent.被动技能.上挑.启用 && Key.isDown(_parent.A键))
		{
			gotoAndPlay("上挑");
		}
	}
}


