onClipEvent (load) {
	_parent.浮空 = true;
	落地 = false;
	if (_parent.temp_y > 0)
	{
		_parent.起始Y = _parent.Z轴坐标;
	}
	else
	{
		_parent.垂直速度 = _parent.起跳速度;
		//_parent.起始Y = _parent._y;
		_parent.起始Y = _parent.Z轴坐标;
	}
	if (_parent._name == _root.控制目标 && !_root.限制系统.DisableKnockdownProtection)
	{
		_parent.无敌 = true;
		_parent.防止无限飞 = true;
	}
	this.onEnterFrame = function()
	{
		if (_parent.硬直中 == false)
		{
			_parent.浮空 = true;
			_parent._y += _parent.垂直速度;
			_parent.垂直速度 += _root.重力加速度;
			if (_parent._y >= _parent.Z轴坐标)
			{
				//_parent._y = _parent.起始Y;
				_parent._y = _parent.Z轴坐标;
				落地 = true;
				delete this.onEnterFrame;
				if (this.受身反制)
				{
					_parent.动画完毕();
				}
				else
				{
					_parent.状态改变("倒地");
				}
			}
		}
		if (_parent.hp > 0 && (_parent.名字 == "Andy" || _parent.名字 == "Blue" || _parent.名字 == "Boy") && _parent._name != _root.控制目标)
		{
			temp_随机小跳值 = random(15);//_root.发布消息("NPC:"+_parent.名字);
			if (temp_随机小跳值 == 0)
			{
				_parent.技能名 = "小跳";
				_parent.状态改变("技能");
				_parent.man.gotoAndPlay("小跳");
			}
			else if (temp_随机小跳值 == 1 || temp_随机小跳值 == 2)
			{
				_parent.技能名 = "上下跳";
				_parent.状态改变("技能");
				_parent.man.gotoAndPlay("上下跳");
			}
			else if (temp_随机小跳值 == 3 && _parent.名字 != "Boy")
			{
				_parent.技能名 = "前跳";
				_parent.状态改变("技能");
				_parent.man.gotoAndPlay("前跳");
			}
		}
	};
	this.onUnload = function()
	{
		_parent.浮空 = false;
		if (_parent._name == _root.控制目标)
		{
			_parent.无敌 = false;
			_parent.防止无限飞 = false;
		}
	};
   	gotoAndPlay(_parent.攻击模式);
}

onClipEvent (load) {
	_parent._y = _parent.Z轴坐标;
	_parent.倒地 = true;
	_parent.格斗架势 = true;
	if (_parent._name == _root.控制目标 && !_root.限制系统.DisableKnockdownProtection)
	{
		_parent.无敌 = true;
		_parent.防止无限飞 = true;
	}
	this.onUnload = function()
	{
		_parent.倒地 = false;
		if (_parent._name == _root.控制目标)
		{
			_parent.无敌 = false;
			_parent.防止无限飞 = false;
		}
	};
}

