}

onClipEvent (load) {
	_parent.格斗架势 = true;
	if (_parent.技能名.indexOf("拳") > -1)
	{
		_parent.根据模式重新读取武器加成("空手");
	}
	else
	{
		_parent.根据模式重新读取武器加成("技能");
	}
	gotoAndPlay(_parent.技能名);
	this.onUnload = function()
	{
		_parent.无敌 = false;
		if (_parent.状态 != "战技")
		{
			_parent.temp_y = 0;
		}
		_parent.UpdateBigSmallState("技能结束","技能结束");
		_parent.根据模式重新读取武器加成(_parent.攻击模式);
	};
}

onClipEvent (load) {
	_parent.格斗架势 = true;
	if (_parent.hp <= 0)
	{
		_parent.状态改变("血腥死");
	}
	else
	{
		if (_parent.技能名.indexOf("拳") > -1)
		{
			_parent.根据模式重新读取武器加成("空手");
		}
		else
		{
			_parent.根据模式重新读取武器加成("技能");
		}
		gotoAndPlay(_parent.技能名);
	}
	this.onUnload = function()
	{
		_parent.无敌 = false;
		_parent.temp_y = 0;
		_parent.UpdateBigSmallState("技能结束","技能结束");
		_parent.根据模式重新读取武器加成(_parent.攻击模式);
	};
}

