onClipEvent (load) {
	if (_parent._name == _root.gameworld[_root.控制目标]._name)
	{
		_parent.读取当前飞行状态();
	}
	if (_parent.格斗架势 == false)
	{
		gotoAndStop("站立");
		play();
	}
	else if (_parent.格斗架势 == true)
	{
		gotoAndStop("格斗架势");
		play();
	}
}

onClipEvent (enterFrame) {
	//if(Key.isDown(_parent.A键) and this._currentframe == 1)
	/*   if(Key.isDown(_parent.A键) and this._currentframe <= 10)
	   {
	      this.gotoAndPlay("空中攻击");
	      跳砍许可 = true;
	      _parent.联机2015发送角色数据();
	   }*/
	/*
	   if(this._currentframe == 10)
	   {
	      跳砍许可 = false;
	   }
	   if(this._currentframe > 1 and !跳砍许可)
	   {
	      this.gotoAndStop(1);
	   }
	   */
	if (!落地)
	{
		_parent._y += _parent.垂直速度;
		_parent.垂直速度 += _root.重力加速度;
	}
	//if (_parent._y >= _parent.起始Y)
	if (_parent._y >= _parent.Z轴坐标)
	{
		//_parent._y = _parent.起始Y;
		_parent._y = _parent.Z轴坐标;
		落地 = true;
		if (!坠地中 || this._currentframe < 77)
		{
			_root.效果("灰尘1",_parent._x,_parent._y,_parent._xscale);
			_root.播放音效("soundland.wav");
			_parent.动画完毕();
		}
	}
	if (!落地)
	{
		if (_parent.跳跃中上下方向 == "上")
		{
			_parent.跳跃上下移动("上",_parent.跳横移速度 * 跳跃移动倍率 / 2);
		}
		else if (_parent.跳跃中上下方向 == "下")
		{
			_parent.跳跃上下移动("下",_parent.跳横移速度 * 跳跃移动倍率 / 2);
		}
		if (_parent.跳跃中左右方向 == "右")
		{
			_parent.移动("右",_parent.跳横移速度 * 跳跃移动倍率);
		}
		else if (_parent.跳跃中左右方向 == "左")
		{
			_parent.移动("左",_parent.跳横移速度 * 跳跃移动倍率);
		}
		if (Key.isDown(_parent.右键) == true)
		{
			_parent.跳跃中左右方向 = "右";
			_parent.方向改变("右");
			_parent.左行 = 0;
			_parent.右行 = 1;
			if (_parent.跳横移速度 == 0)
			{
				_parent.移动("右",_parent.跳跃中移动速度 * 跳跃移动倍率);
			}
		}
		else if (Key.isDown(_parent.左键) == true)
		{
			_parent.跳跃中左右方向 = "左";
			_parent.方向改变("左");
			_parent.右行 = 0;
			_parent.左行 = 1;
			if (_parent.跳横移速度 == 0)
			{
				_parent.移动("左",_parent.跳跃中移动速度 * 跳跃移动倍率);
			}
		}
		else
		{
			_parent.跳跃中左右方向 = "无";
		}
		if (Key.isDown(_parent.上键) == true)
		{
			_parent.跳跃中上下方向 = "上";
			if (_parent.跳横移速度 == 0)
			{
				_parent.跳跃上下移动("上",_parent.跳跃中移动速度 * 跳跃移动倍率 / 2);
			}
		}
		else if (Key.isDown(_parent.下键) == true)
		{
			_parent.跳跃中上下方向 = "下";
			if (_parent.跳横移速度 == 0)
			{
				_parent.跳跃上下移动("下",_parent.跳跃中移动速度 * 跳跃移动倍率 / 2);
			}
		}
		else
		{
			_parent.跳跃中上下方向 = "无";
		}
	}
}
onClipEvent (load) {
	跳跃移动倍率 = 1;
	落地 = false;
	坠地中 = false;
	_parent.浮空 = true;
	if (_root.技能浮空 == true && _parent._name == _root.控制目标)
	{
		_root.技能浮空 = false;
		_parent.起始Y = _parent.Z轴坐标;
	}
	else if (_parent.temp_y > 0)
	{
		_parent.起始Y = _parent.Z轴坐标;
	}
	else
	{
		_parent.垂直速度 = _parent.起跳速度;
		//_parent.起始Y = _parent._y;
		_parent.起始Y = _parent.Z轴坐标;
	}
	//stop();
	if (_parent._name == _root.控制目标)
	{
		//_parent.读取当前飞行状态();
		if (_parent.hp > 0 && _parent.被动技能.上挑 && _parent.被动技能.上挑.启用 && Key.isDown(_parent.A键))
		{
			gotoAndPlay("上挑");
		}
	}
	this.onUnload = function()
	{
		坠地中 = false;
		_parent.浮空 = false;
	};
}


